    /* text color */
    --grayOut: #8b8b8b;
    /* grayed out text and element color */
    --highlight: #fd810d;
    /* accent color, link color */
    --btnHighlight: #fd810d;
    /* modal highlight button */
    --windowClose: #e81123;
    /* window close */
    --warn: #cf5d6e;
    /* deleted file title */

    /* You can specify one or more fonts to be used in the editor in order of priority.
          Using Fira Code as the default editor font.
        */
    --editor-font: "Fira Code", monospace;

    /* text, syntax highlight color */
    /* style: bold italic underline (set with space if multiple) */
    --heading1: var(--highlight);
    /* heading 1 */
    --heading1Style: ;
    --heading2: #d9bea0;
    /* heading 2 */
    --heading2Style: ;
    --heading3: var(--editorText);
    /* heading 3 */
    --heading3Style: ;
    --bulletPoint: var(--highlight);
    /* bullet point */
    --bulletPointStyle: ;
    --numberList: var(--highlight);
    /* bullet point */
    --numberListStyle: ;
    --subText: var(--grayOut);
    /* sub text */
    --subTextStyle: ;
    --blockQuote: var(--editorText);
    /* block quote */
    --blockQuoteStyle: italic;
    --inlineCode: var(--warn);
    /* inline code */
    --inlineCodeStyle: ;
    --codeBlockFence: #5b5b5b;
    /* code block fence */
    --codeBlockFenceStyle: ;
    --codeBlock: #9b8363;
    /* code block */
    --codeBlockStyle: ;

    /* monaco editor color */
    --vscode-editor-background: var(--color1);
    --vscode-editor-foreground: var(--editorText);
    --vscode-errorForeground: var(--warn);
    --vscode-inputValidation-errorBorder: var(--warn);
    --vscode-editor-lineHighlightBackground: var(--color2);
    --vscode-editor-lineHighlightBorder: #00000000;
    --vscode-editor-wordHighlightBackground: #00000000;
    --vscode-editorWidget-background: var(--color2);
    --vscode-editorHoverWidget-background: var(--color2);
    --vscode-input-background: var(--color1);
    --vscode-list-activeSelectionBackground: var(--color3);
    --vscode-list-dropBackground: var(--color3);
    --vscode-menu-selectionBackground: var(--color3);
    --vscode-list-hoverBackground: var(--color3);
    --vscode-editorStickyScrollHover-background: var(--color3);
    --vscode-button-hoverBackground: var(--color3);
    --vscode-badge-background: var(--color3);
    --vscode-editorWidget-border: var(--color3);
    --vscode-editorHoverWidget-border: var(--color3);
    --vscode-editorGutter-foldingControlForeground: var(--grayOut);
    --vscode-editorLineNumber-foreground: #8b8b8b31;
    --vscode-editorLineNumber-activeForeground: var(--grayOut);
    --vscode-editor-selectionBackground: #d6945644;
    --vscode-editor-findMatchBackground: #d18616a1;
    --vscode-pickerGroup-foreground: var(--grayOut);
    --vscode-quickInputList-focusBackground: var(--color3);
    --vscode-symbolIcon-classForeground: var(--highlight);
    --vscode-symbolIcon-functionForeground: #d9bea0;
    --vscode-symbolIcon-methodForeground: var(--editorText);
    --vscode-quickInputList-focusIconForeground: var(--editorText);
    --vscode-quickInput-foreground: var(--grayOut);
    --vscode-quickInputList-focusForeground: var(--editorText);
}

body {
    margin: 0;
    font-family: "Inter", "Yu Gothic UI", "Meiryo", "Hiragino Sans", sans-serif;
    background: var(--color1);
    color: var(--editorText);
    overflow: hidden;
}

/* monaco editor style */
.monaco-editor .bracket-highlighting-0,
.monaco-editor .bracket-highlighting-1,
.monaco-editor .bracket-highlighting-2 {
    color: #ce9178 !important;
}

.monaco-editor .marker-transparent {
    opacity: 0.25;
}

.monaco-editor .unexpected-closing-bracket {
    color: var(--editorText) !important;
}

.monaco-editor .line-numbers {
    font-family: "Consolas", "Courier New", monospace !important;
    user-select: none;
}

.monaco-editor .scroll-decoration {
    height: 10px !important;
    box-shadow: var(--color1) 0 6px 6px 0px inset !important;
    pointer-events: none;
}

.monaco-editor .minimap-shadow-visible {
    width: 10px !important;
    left: -10px !important;
    box-shadow: var(--color1) -6px 0 6px 0px inset !important;
    pointer-events: none;
}

:focus-visible {
    outline: var(--highlight) solid 1px;
    border-radius: 0px;
}

.monaco-action-bar.quick-input-inline-action-bar {
    display: none !important;
}

.monaco-editor .margin-view-overlays .codicon-folding-manual-collapsed,
.monaco-editor .margin-view-overlays .codicon-folding-manual-expanded,
.monaco-editor .margin-view-overlays .codicon-folding-expanded,
.monaco-editor .margin-view-overlays .codicon-folding-collapsed {
    transition: none !important;
}

button 3d.content:
button {
    font: inherit;
    background-color: #2a2a2a;
    border: 0;
    color: #e0e0e0;
    border-radius: 0;
    font-size: 1.35rem;
    padding: 0.375em 1em;
    font-weight: 600;
