moj styl.content:
:root {
            --bg: #f8f9fa;
            --panel-bg: #ffffff;
            --text-color: #212529;
            --text-muted: #6c757d;
            --highlight-color: #ff7300;
            --border-color: #dee2e6;
            --card-bg: #f8f9fa;
            --status-active-bg: #28a745;
        }

        :root[theme="dark"] {
            --bg: #121212;
            --panel-bg: #000000;
            --text-color: #e9ecef;
            --text-muted: #8d8d8d;
            --highlight-color: #ff7300;
            --border-color: #2a2a2a;
            --card-bg: #1a1a1a;
            --status-active-bg: var(--highlight-color);
        }

        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
            font-family: "JetBrains Mono", "Share Tech Mono", monaco, courier;
        }

        body {
            background: var(--bg);
            color: var(--text-color);
            height: 100vh;
            width: 100vw;
            overflow: hidden;
        }

        .command-center-grid {
            display: grid;
            grid-template-columns: 1fr 1fr;
            grid-template-rows: 1fr 1fr;
            gap: 0.5rem;
            width: 100%;
            height: 100%;
            padding: 0.5rem;
        }

        .panel {
            background: var(--panel-bg);
            border: 1px solid var(--border-color);
            display: flex;
            flex-direction: column;
            overflow: hidden;
            position: relative;
            padding-top: 40px;
            /* Space for the header */
        }

        .panel-header {
            background: var(--highlight-color);
            color: #000;
            padding: 0.25rem 0.5rem;
            font-size: 0.65rem;
            font-weight: 700;
            text-transform: uppercase;
            position: absolute;
            top: 10px;
            left: 10px;
            height: 24px;
            line-height: 1.5;
            z-index: 10;
        }

        .panel-header-secondary {
            background: var(--panel-bg);
            color: var(--text-muted);
            border: 1px solid var(--border-color);
        }

        .panel-content {
            padding: 0.5rem;
            flex-grow: 1;
            overflow-y: auto;
            display: grid;
            grid-template-columns: 1fr 1fr;
            grid-template-rows: 1fr 1fr;
            gap: 0.5rem;
        }

        .info-card {
            background: var(--card-bg);
            border: 1px solid var(--border-color);
            padding: 0.5rem 1rem;
            display: flex;
            flex-direction: column;
            justify-content: space-between;
            text-decoration: none;
            color: var(--text-color);
            transition: background-color 0.2s, border-color 0.2s;
        }

        .info-card:hover {
            border-color: var(--highlight-color);
            background-color: rgba(255, 115, 0, 0.05);
        }

        .card-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            font-size: 0.7rem;
            margin-bottom: 0.25rem;
            color: var(--text-muted);
        }

        .card-status {
            font-weight: 700;
            font-size: 0.6rem;
            text-transform: uppercase;
        }

        .card-status.active {
            color: var(--status-active-bg);
        }

        .card-body {
            display: flex;
            align-items: flex-start;
            gap: 0.7rem;
            padding-top: 0.5rem;
        }

        .card-icon {
            font-size: 1.5rem;
            color: var(--text-muted);
            width: 20px;
            text-align: center;
        }

        .card-title-section {
            flex-grow: 1;
        }

        .card-title {
            font-weight: 700;
            font-size: 1.2rem;
            line-height: 1.2;
        }

        .card-subtitle {
            font-size: 0.75rem;
            color: var(--text-muted);
        }

        /* Carousel Styles */
        .carousel-panel {
            padding: 0;
            position: relative;
        }

        .carousel-slides {
            display: flex;
            height: 100%;
            width: 100%;
            transition: transform 0.5s ease-in-out;
        }

        .carousel-slide {
            flex-shrink: 0;
            width: 100%;
            height: 100%;
            background-size: cover;
            background-position: center;
            position: relative;
        }

        .visual-panel-overlay {
            background: linear-gradient(to top, rgba(0, 0, 0, 0.9), transparent);
            position: absolute;
            bottom: 0;
            left: 0;
            right: 0;
            padding: 1rem;
            color: #fff;
            width: 100%;
        }

        .visual-title {
            font-size: 1.25rem;
            font-weight: 700;
            color: var(--highlight-color);
        }

        .visual-subtitle {
            font-size: 0.9rem;
            opacity: 0.8;
            max-width: 80%;
        }

        .carousel-nav {
            position: absolute;
            bottom: 1rem;
            right: 1rem;
            z-index: 10;
        }

        .carousel-btn {
            background-color: rgba(0, 0, 0, 0.5);
            color: white;
            border: 1px solid var(--border-color);
            padding: 0.5rem;
            cursor: pointer;
            margin-left: 0.5rem;
        }

        .carousel-btn:hover {
            background-color: var(--highlight-color);
            color: #000;
        }


        .nav-controls {
            position: fixed;
            bottom: 20px;
            left: 20px;
            z-index: 1000;
        }

        .nav-btn {
            background: var(--highlight-color);
            color: #000;
            border: 1px solid var(--border-color);
            padding: 10px;
            width: 50px;
            height: 40px;
            cursor: pointer;
            font-family: inherit;
            font-size: 10px;
            font-weight: bold;
            transition: all 0.3s ease;
            text-transform: uppercase;
        }

        .nav-btn:hover {
            background: #e65c00;
            border-color: #e65c00;
        }

        .comm-panel-content {
            display: grid;
            grid-template-columns: 1fr 1fr;
            grid-template-rows: 1fr 1fr;
            gap: 0.5rem;
            flex-grow: 1;
            overflow-y: auto;
            padding: 0.5rem;
        }

        .comm-panel-content .info-card {
            grid-column: span 1;
        }

        .comm-panel-content .info-card:first-child {
            grid-row: span 2;
            justify-content: center;
        }

        .comm-panel-content .info-card.card-accent {
            background: var(--highlight-color);
            color: #000;
        }

        .comm-panel-content .info-card.card-accent .card-title,
        .comm-panel-content .info-card.card-accent .card-subtitle,
        .comm-panel-content .info-card.card-accent .card-icon,
        .comm-panel-content .info-card.card-accent .card-header {
            color: #000;
        }

Handsontable styl.content:
/* --- Handsontable Theming --- */

/* Apply ht-theme-main only when light theme is active */
:root[data-theme="light"] .ht-theme-main {
    /* Custom variable overrides for the light theme to match our UI */
    --ht-theme-main-background: var(--panel-bg);
    --ht-theme-main-color: var(--text-color);
    --ht-theme-main-border-color: var(--border-color);
    --ht-theme-main-header-background: var(--card-bg);
    --ht-theme-main-accent-color: var(--highlight-color);
    --ht-theme-main-active-border-color: var(--highlight-color);
}

/* Custom styling for dark themes */
:root[data-theme="dark"] .handsontable,
:root[data-theme="cyberpunk"] .handsontable,
:root[data-theme="terminal"] .handsontable {
    background-color: var(--card-bg) !important;
}

.handsontable,
.handsontable .htCore {
    background-color: var(--panel-bg);
    color: var(--text-color);
}

.handsontable th,
.handsontable td {
    border-color: var(--border-color) !important;
    background-color: var(--card-bg);
}

.handsontable th {
    font-weight: 600;
    color: var(--text-muted);
}

.handsontable .htCore .hot-search-result {
    background-color: rgba(255, 115, 0, 0.2) !important;
}

.handsontable .htCore .current-row,
.handsontable .htCore .current-col {
    background-color: var(--card-bg) !important;
}

.handsontable .htCommentCell::after {
    border-top-color: var(--highlight-color) !important;
}

.handsontable .htInvalid {
    background-color: rgba(220, 53, 69, 0.1) !important;
}

/* --- NEW: Improved Custom Context Menu Styling for ALL Dark Themes --- */
:is(:root[data-theme="dark"], :root[data-theme="cyberpunk"], :root[data-theme="terminal"]) .htContextMenu,
:is(:root[data-theme="dark"], :root[data-theme="cyberpunk"], :root[data-theme="terminal"]) .htDropdownMenu {
    background-color: #080808 !important;
    /* Slightly off-black for depth */
    border: 1px solid var(--border-color) !important;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.5) !important;
    color: var(--text-muted) !important;
}

:is(:root[data-theme="dark"], :root[data-theme="cyberpunk"], :root[data-theme="terminal"]) .htContextMenu .htCore td,
:is(:root[data-theme="dark"], :root[data-theme="cyberpunk"], :root[data-theme="terminal"]) .htDropdownMenu .htCore td {
    background-color: var(--card-bg) !important;
    /* color: inherit !important; */
    border-color: var(--border-color) !important;
    font-weight: 500;
}

/* Hover state for menu items */
:is(:root[data-theme="dark"], :root[data-theme="cyberpunk"], :root[data-theme="terminal"]) .htContextMenu .htItem:not(.htSeparator):hover>td,
:is(:root[data-theme="dark"], :root[data-theme="cyberpunk"], :root[data-theme="terminal"]) .htDropdownMenu .htItem:not(.htSeparator):hover>td {
    background-color: var(--highlight-color) !important;
    color: #000 !important;
}

/* Submenu arrow */
:is(:root[data-theme="dark"], :root[data-theme="cyberpunk"], :root[data-theme="terminal"]) .htContextMenu .htSubmenuArrow {
    color: inherit !important;
}

:is(:root[data-theme="dark"], :root[data-theme="cyberpunk"], :root[data-theme="terminal"]) .htContextMenu .htItem:not(.htSeparator):hover>td .htSubmenuArrow {
    color: #000 !important;
}

/* Separator */
:is(:root[data-theme="dark"], :root[data-theme="cyberpunk"], :root[data-theme="terminal"]) .htContextMenu .htSeparator td {
    background-color: var(--border-color) !important;
    height: 1px !important;
    padding: 0 !important;
    margin: 4px 0 !important;
    border: none !important;
}

/* Disabled items */
:is(:root[data-theme="dark"], :root[data-theme="cyberpunk"], :root[data-theme="terminal"]) .htContextMenu .htDisabled>td,
:is(:root[data-theme="dark"], :root[data-theme="cyberpunk"], :root[data-theme="terminal"]) .htContextMenu .htDisabled:hover>td {
    background-color: #111 !important;
    color: #313131 !important;
    cursor: default;
}

/* Dropdown menu specific (filters) */
:is(:root[data-theme="dark"], :root[data-theme="cyberpunk"], :root[data-theme="terminal"]) .htDropdownMenu .ht_filters_menu a {
    color: var(--text-muted) !important;
    padding: 4px 8px !important;
}

:is(:root[data-theme="dark"], :root[data-theme="cyberpunk"], :root[data-theme="terminal"]) .htDropdownMenu .ht_filters_menu a:hover {
    background-color: var(--highlight-color) !important;
    color: #000 !important;
}

:is(:root[data-theme="dark"], :root[data-theme="cyberpunk"], :root[data-theme="terminal"]) .htDropdownMenu .ht_filters_input,
:is(:root[data-theme="dark"], :root[data-theme="cyberpunk"], :root[data-theme="terminal"]) .htDropdownMenu .ht_filters_input input {
    background-color: var(--border-color) !important;
    color: var(--text-color) !important;
    border: 1px solid #333 !important;
}

