select style.content:
/* *
         * Custom Select/Dropdown Styling 
         */
.custom-select {
    position: relative;
    display: inline-block;
    margin-right: 0.2rem;
    min-width: 120px;
}

.select-trigger {
    background-color: var(--card-bg);
    color: var(--text-color);
    border: 1px solid var(--border-color);
    padding: 0.7rem 1rem;
    cursor: pointer;
    font-size: 0.85rem;
    font-weight: 700;
    font-family: "JetBrains Mono", monospace;
    transition: all 0.2s ease;
    outline: none;
    display: flex;
    align-items: center;
    justify-content: space-between;
    user-select: none;
    width: 180px;
    position: relative;
    overflow: hidden;
}

.select-trigger:hover {
    background-color: #222;
    border-color: #444;
    color: var(--text-color);
}

.select-trigger.active {
    border-color: var(--highlight-color);
    background-color: #222;
    box-shadow: 0 0 0 2px rgba(255, 115, 0, 0.2);
}

.select-trigger i {
    font-size: 0.8rem;
    transition: transform 0.2s ease;
    margin-left: 0.5rem;
}

.select-trigger.active i {
    transform: rotate(180deg);
}

.select-options {
    position: absolute;
    top: 100%;
    left: 0;
    right: 0;
    background-color: var(--panel-bg);
    border: 1px solid var(--border-color);
    border-top: none;
    max-height: 250px;
    overflow-y: auto;
    z-index: 1000;
    display: none;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.3);
}

.select-options.show {
    display: block;
}

.option {
    background-color: var(--panel-bg);
    color: var(--text-muted);
    padding: 0.5rem 1rem;
    font-family: "JetBrains Mono", monospace;
    font-size: 0.85rem;
    font-weight: 500;
    border-left: 2px solid transparent;
    cursor: pointer;
    transition: all 0.15s ease;
    user-select: none;
    height: 34px;
    display: flex;
    position: relative;
    overflow: hidden;
}

.option i {
    font-size: 0.9rem;
    width: 16px;
    text-align: center;
    color: var(--highlight-color);
    margin-right: 0.8rem;
}

.option:hover {
    background-color: var(--card-bg);
    color: var(--text-color);
    border-left: 2px solid var(--highlight-color);
}

.option.selected {
    background-color: rgba(255, 115, 0, 0.1);
    color: var(--highlight-color);
    border-left-color: var(--highlight-color);
    font-weight: 700;
}

.option:last-child {
    border-bottom: none;
}

.toolbar .fas {
    font-size: 1rem;
}

