class TimeFormatter {
    static parseTimeString(timeString) {
        if (!timeString || typeof timeString !== "string")
            return { days: 0, hours: 0, minutes: 0 };
        // Handle both formats: "8 day(s), 6 hour(s), 18 minute(s)" and "8d(s), 6h(s), 18m(s)"
        const daysMatch = timeString.match(/(\d+)\s*d(?:ay)?(?:\(s\))?/i);
        const hoursMatch = timeString.match(/(\d+)\s*h(?:our)?(?:\(s\))?/i);
        const minutesMatch = timeString.match(/(\d+)\s*m(?:inute)?(?:\(s\))?/i);
        return {
            days: daysMatch ? parseInt(daysMatch[1], 10) : 0,
            hours: hoursMatch ? parseInt(hoursMatch[1], 10) : 0,
            minutes: minutesMatch ? parseInt(minutesMatch[1], 10) : 0,
        };
    }
    static formatTimeString(timeString) {
        if (!timeString) return "—";
        const time = this.parseTimeString(timeString);
        return `${time.days}d (s), ${time.hours}h(s), ${time.minutes}m (s)`;
    }
    static getMinutes(timeString) {
        const time = this.parseTimeString(timeString);
        return time.days * 24 * 60 + time.hours * 60 + time.minutes;
    }
}

class CSVAnalyzer {
    constructor() {
        this.data = [];
        this.filteredData = [];
        this.headers = [];
        this.filters = {};
        this.sortColumn = "TIME IN YARD";
        this.sortDirection = "desc";
        this.searchTerm = "";
        this.currentPage = 1;
        this.itemsPerPage = 50;
        this.currentPageData = [];
        this.currentDetailedViewData = [];
        this.viewMode = "table"; // 'table' lub 'tiles'
        this.summaryViewMode = "table"; // 'table' lub 'tiles' dla widoku szczegółowego w Summary

        // Bufor dla załadowanych plików CSV
        this.csvBuffer = new Map();
        this.currentFileName = null;
        this.maxBufferSize = 5; // Maksymalna liczba plików w buforze

        // Auto-refresh settings
        this.autoRefreshEnabled = false;
        this.autoRefreshInterval = null;
        this.autoRefreshURL = "";
        this.autoRefreshIntervalMinutes = 5;
        this.useCorsProxy = false;

        this.elements = {
            uploadSection: document.getElementById("upload-section"),
            csvFile: document.getElementById("csvFile"),
            fileName: document.getElementById("file-name"),
            filterContainer: document.getElementById("filterContainer"),
            searchInput: document.getElementById("searchInput"),
            tableContainer: document.getElementById("tableContainer"),
            paginationContainer: document.getElementById("paginationContainer"),
            summaryContainer: document.getElementById("summaryContainer"),
            themeToggleBtn: document.getElementById("theme-toggle-btn"),
            viewToggleBtn: document.getElementById("view-toggle-btn"),
            bufferModalBtn: document.getElementById("buffer-modal-btn"),
            bufferModal: document.getElementById("buffer-modal"),
            closeBufferModal: document.getElementById("close-buffer-modal"),
            currentFileName: document.getElementById("current-file-name"),
            bufferCount: document.getElementById("buffer-count"),
            bufferFilesList: document.getElementById("buffer-files-list"),
            clearAllBuffer: document.getElementById("clear-all-buffer"),
            saveModalBtn: document.getElementById("save-modal-btn"),
            saveModal: document.getElementById("save-modal"),
            closeSaveModal: document.getElementById("close-save-modal"),
            saveAllData: document.getElementById("save-all-data"),
            saveFilteredData: document.getElementById("save-filtered-data"),
            saveBufferState: document.getElementById("save-buffer-state"),
            loadBufferState: document.getElementById("load-buffer-state"),
            exportFilename: document.getElementById("export-filename"),
            includeHeaders: document.getElementById("include-headers"),
            totalRecords: document.getElementById("total-records"),
            filteredRecords: document.getElementById("filtered-records"),
            summaryModalBtn: document.getElementById("summary-modal-btn"),
            summaryModal: document.getElementById("summary-modal"),
            closeSummaryModal: document.getElementById("close-summary-modal"),
            summaryModalContent: document.getElementById("summary-modal-content"),
            tabBtns: document.querySelectorAll(".tab-btn"),
            summaryTableContainer: document.getElementById("summaryTableContainer"),
            detailsModal: document.getElementById("details-modal"),
            closeDetailsModal: document.getElementById("close-details-modal"),
            detailsModalContent: document.getElementById("details-modal-content"),
            useCorsProxyCheckbox: document.getElementById("use-cors-proxy"),
            confirmModal: document.getElementById("confirm-modal"),
            closeConfirmModal: document.getElementById("close-confirm-modal"),
            confirmModalTitle: document.getElementById("confirm-modal-title"),
            confirmModalMessage: document.getElementById("confirm-modal-message"),
            confirmModalButtons: document.getElementById("confirm-modal-buttons"),
        };

        this.init();
    }

    init() {
        this.elements.csvFile.addEventListener("change", (e) =>
            this.handleFileUpload(e.target.files[0])
        );
        this.elements.searchInput.addEventListener("input", (e) =>
            this.handleSearch(e.target.value)
        );

        const dropZone = this.elements.uploadSection;
        dropZone.addEventListener("dragover", (e) => {
            e.preventDefault();
            dropZone.classList.add("drop-zone-active");
        });
        dropZone.addEventListener("dragleave", () =>
            dropZone.classList.remove("drop-zone-active")
        );
        dropZone.addEventListener("drop", (e) => {
            e.preventDefault();
            dropZone.classList.remove("drop-zone-active");
            const file = e.dataTransfer.files[0];
            if (file) {
                this.elements.csvFile.files = e.dataTransfer.files;
                this.handleFileUpload(file);
            }
        });

        this.elements.themeToggleBtn.addEventListener("click", () => {
            const html = document.documentElement;
            const currentTheme = html.getAttribute("theme");
            html.setAttribute("theme", currentTheme === "dark" ? "light" : "dark");
        });

        this.elements.viewToggleBtn.addEventListener("click", () =>
            this.toggleView()
        );

        // Event listenery dla auto-refresh
        const refreshUrlBtn = document.getElementById("refresh-url-btn");
        const toggleAutoRefreshBtn = document.getElementById("toggle-auto-refresh");
        const manualRefreshBtn = document.getElementById("manual-refresh-url");
        const autoRefreshUrlInput = document.getElementById("auto-refresh-url");
        const autoRefreshIntervalInput = document.getElementById(
            "auto-refresh-interval"
        );

        refreshUrlBtn.addEventListener("click", () => this.promptAndFetchURL());

        toggleAutoRefreshBtn.addEventListener("click", () => {
            this.toggleAutoRefresh();
            this.updateAutoRefreshUI();
        });

        manualRefreshBtn.addEventListener("click", () => {
            const url = autoRefreshUrlInput.value;
            if (url) {
                this.setAutoRefreshURL(url);
                this.fetchCSVFromURL(url);
            } else {
                this.showAlert("Podaj URL pliku CSV", "warning");
            }
        });

        autoRefreshUrlInput.addEventListener("change", (e) => {
            this.setAutoRefreshURL(e.target.value);
        });

        autoRefreshIntervalInput.addEventListener("change", (e) => {
            const minutes = parseInt(e.target.value, 10);
            if (minutes > 0) {
                this.setAutoRefreshInterval(minutes);
            }
        });

        this.elements.useCorsProxyCheckbox.addEventListener("change", (e) => {
            this.useCorsProxy = e.target.checked;
            localStorage.setItem("csv_use_cors_proxy", this.useCorsProxy);
            console.log(
                `CORS Proxy: ${this.useCorsProxy ? "włączony" : "wyłączony"}`
            );
        });

        // Event listener dla kliknięć w karty podsumowania
        this.elements.summaryContainer.addEventListener("click", (e) =>
            this.handleSummaryCardClick(e)
        );

        // Event listenery dla modala bufora
        this.elements.bufferModalBtn.addEventListener("click", () =>
            this.openBufferModal()
        );
        this.elements.closeBufferModal.addEventListener("click", () =>
            this.closeBufferModal()
        );
        this.elements.clearAllBuffer.addEventListener("click", () =>
            this.clearAllBufferFiles()
        );

        // Event listenery dla modala zapisu
        this.elements.saveModalBtn.addEventListener("click", () =>
            this.openSaveModal()
        );
        this.elements.closeSaveModal.addEventListener("click", () =>
            this.closeSaveModal()
        );
        this.elements.saveAllData.addEventListener("click", () =>
            this.exportData("all")
        );
        this.elements.saveFilteredData.addEventListener("click", () =>
            this.exportData("filtered")
        );
        this.elements.saveBufferState.addEventListener("click", () =>
            this.saveBufferToStorage()
        );
        this.elements.loadBufferState.addEventListener("click", () =>
            this.loadBufferFromStorage()
        );

        // Event listenery dla modala podsumowania
        this.elements.summaryModalBtn.addEventListener("click", () =>
            this.openSummaryModal()
        );
        this.elements.closeSummaryModal.addEventListener("click", () =>
            this.closeSummaryModal()
        );

        // Event listenery dla modala szczegółów
        this.elements.closeDetailsModal.addEventListener("click", () =>
            this.closeDetailsModal()
        );

        // Event listenery dla modala potwierdzenia
        this.elements.closeConfirmModal.addEventListener("click", () =>
            this.closeConfirmModal()
        );
        this.elements.tableContainer.addEventListener("click", (e) => {
            const row = e.target.closest("tbody tr");
            if (row && row.dataset.rowIndex) {
                const rowIndex = parseInt(row.dataset.rowIndex, 10);
                if (!isNaN(rowIndex) && this.currentPageData[rowIndex]) {
                    this.openDetailsModal(this.currentPageData[rowIndex]);
                }
            }
        });

        this.elements.summaryTableContainer.addEventListener("click", (e) => {
            const row = e.target.closest("tbody tr");
            // Upewnij się, że kliknięto wiersz z danymi w widoku szczegółowym, a nie wiersz kategorii podsumowania
            if (
                row &&
                row.dataset.rowIndex &&
                !row.classList.contains("summary-row")
            ) {
                const rowIndex = parseInt(row.dataset.rowIndex, 10);
                if (!isNaN(rowIndex) && this.currentDetailedViewData[rowIndex]) {
                    this.openDetailsModal(this.currentDetailedViewData[rowIndex]);
                }
            }
        });

        // Event listenery dla zakładek
        this.elements.tabBtns.forEach((btn) => {
            btn.addEventListener("click", (e) =>
                this.switchTab(e.target.dataset.tab)
            );
        });

        // Zamknij modali po kliknięciu w tło
        this.elements.bufferModal.addEventListener("click", (e) => {
            if (e.target === this.elements.bufferModal) this.closeBufferModal();
        });
        this.elements.saveModal.addEventListener("click", (e) => {
            if (e.target === this.elements.saveModal) this.closeSaveModal();
        });
        this.elements.summaryModal.addEventListener("click", (e) => {
            if (e.target === this.elements.summaryModal) this.closeSummaryModal();
        });
        this.elements.detailsModal.addEventListener("click", (e) => {
            if (e.target === this.elements.detailsModal) this.closeDetailsModal();
        });
        this.elements.confirmModal.addEventListener("click", (e) => {
            if (e.target === this.elements.confirmModal) this.closeConfirmModal();
        });

        // Zamknij modali klawiszem Escape
        document.addEventListener("keydown", (e) => {
            if (e.key === "Escape") {
                if (!this.elements.confirmModal.classList.contains("hidden"))
                    this.closeConfirmModal();
                else if (!this.elements.bufferModal.classList.contains("hidden"))
                    this.closeBufferModal();
                else if (!this.elements.saveModal.classList.contains("hidden"))
                    this.closeSaveModal();
                else if (!this.elements.summaryModal.classList.contains("hidden"))
                    this.closeSummaryModal();
                else if (!this.elements.detailsModal.classList.contains("hidden"))
                    this.closeDetailsModal();
            }
        });

        // Załaduj zapisany URL auto-refresh z localStorage
        const savedURL = localStorage.getItem("csv_auto_refresh_url");
        const savedInterval = localStorage.getItem("csv_auto_refresh_interval");
        const savedCorsProxy = localStorage.getItem("csv_use_cors_proxy");
        if (savedURL) this.autoRefreshURL = savedURL;
        if (savedInterval)
            this.autoRefreshIntervalMinutes = parseInt(savedInterval, 10);
        if (savedCorsProxy) this.useCorsProxy = savedCorsProxy === "true";

        // Dodaj globalne metody do debugowania bufora
        window.csvAnalyzer = this;
        window.showBufferInfo = () => {
            console.log("=== INFORMACJE O BUFORZE CSV ===");
            console.log(`Aktualny plik: ${this.currentFileName || "brak"}`);
            console.log(
                `Rozmiar bufora: ${this.csvBuffer.size}/${this.maxBufferSize}`
            );

            if (this.csvBuffer.size > 0) {
                console.table(this.getBufferInfo());
            } else {
                console.log("Bufor jest pusty");
            }
        };
        window.clearCSVBuffer = () => {
            this.clearBuffer();
            console.log("Bufor CSV został wyczyszczony");
        };
    }

    handleFileUpload(file) {
        if (!file) {
            this.elements.fileName.textContent = "*.CSV";
            return;
        }

        const fileName = file.name;
        this.elements.fileName.textContent = fileName;

        // Sprawdź czy plik jest już w buforze
        if (this.csvBuffer.has(fileName)) {
            console.log(`Ładowanie z bufora: ${fileName}`);
            this.loadFromBuffer(fileName);
            return;
        }

        this.elements.tableContainer.innerHTML = `<p class="placeholder-text loading">Przetwarzanie danych...</p>`;

        Papa.parse(file, {
            header: true,
            dynamicTyping: true,
            skipEmptyLines: true,
            complete: (results) => {
                if (results.errors.length) {
                    this.elements.tableContainer.innerHTML = `<p class="placeholder-text error" style="color: var(--status-unsealed-color)">Błąd: ${results.errors[0].message}</p>`;
                    return;
                }
                this.saveToBuffer(fileName, results.data);
                this.processData(results.data);
            },
            error: (error) => {
                this.elements.tableContainer.innerHTML = `<p class="placeholder-text error" style="color: var(--status-unsealed-color)">Błąd odczytu pliku: ${error.message}</p>`;
            },
        });
    }

    saveToBuffer(fileName, data) {
        // Jeśli bufor jest pełny, usuń najstarszy wpis
        if (this.csvBuffer.size >= this.maxBufferSize) {
            const firstKey = this.csvBuffer.keys().next().value;
            this.csvBuffer.delete(firstKey);
            console.log(`Usunięto z bufora: ${firstKey}`);
        }

        // Zapisz dane do bufora z timestampem
        this.csvBuffer.set(fileName, {
            data: data,
            timestamp: Date.now(),
            headers: Object.keys(data[0] || {}),
        });

        this.currentFileName = fileName;
        console.log(`Zapisano do bufora: ${fileName} (${data.length} rekordów)`);
        console.log(
            `Bufor zawiera: ${this.csvBuffer.size}/${this.maxBufferSize} plików`
        );
    }

    loadFromBuffer(fileName) {
        const bufferedData = this.csvBuffer.get(fileName);
        if (!bufferedData) {
            console.error(`Plik ${fileName} nie został znaleziony w buforze`);
            return;
        }

        this.currentFileName = fileName;
        this.processData(bufferedData.data);

        // Aktualizuj timestamp ostatniego użycia
        bufferedData.timestamp = Date.now();
        console.log(
            `Załadowano z bufora: ${fileName} (${bufferedData.data.length} rekordów)`
        );
    }

    clearBuffer() {
        this.csvBuffer.clear();
        this.currentFileName = null;
        console.log("Bufor został wyczyszczony");
    }

    // Pobierz CSV z URL
    async fetchCSVFromURL(url) {
        if (!url || url.trim() === "") {
            this.showAlert("Podaj prawidłowy URL do pliku CSV", "warning");
            return;
        }

        this.elements.tableContainer.innerHTML = `<p class="placeholder-text loading">Pobieranie danych z URL...</p>`;

        try {
            // Użyj CORS proxy jeśli włączone
            let fetchURL = url;
            if (this.useCorsProxy) {
                // Użyj allorigins.win jako CORS proxy - działa dobrze i jest darmowy
                fetchURL = `https://api.allorigins.win/raw?url=${encodeURIComponent(
                    url
                )}`;
                console.log(`Używam CORS Proxy: ${fetchURL}`);
            }

            const response = await fetch(fetchURL);

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const csvText = await response.text();
            const fileName = url.split("/").pop() || "remote_data.csv";

            Papa.parse(csvText, {
                header: true,
                dynamicTyping: true,
                skipEmptyLines: true,
                complete: (results) => {
                    if (results.errors.length) {
                        this.elements.tableContainer.innerHTML = `<p class="placeholder-text error" style="color: var(--status-unsealed-color)">Błąd parsowania: ${results.errors[0].message}</p>`;
                        return;
                    }
                    this.saveToBuffer(fileName, results.data);
                    this.processData(results.data);
                    console.log(
                        `Załadowano ${results.data.length} rekordów z URL ${this.useCorsProxy ? "(przez CORS Proxy)" : ""
                        }`
                    );
                },
                error: (error) => {
                    this.elements.tableContainer.innerHTML = `<p class="placeholder-text error" style="color: var(--status-unsealed-color)">Błąd parsowania CSV: ${error.message}</p>`;
                },
            });
        } catch (error) {
            console.error("Błąd pobierania CSV:", error);
            let errorMessage = `Błąd pobierania: ${error.message}`;
            if (!this.useCorsProxy && error.message.includes("Failed to fetch")) {
                errorMessage +=
                    '<br><br><small>💡 Spróbuj włączyć "Użyj CORS Proxy" w ustawieniach</small>';
            }
            this.elements.tableContainer.innerHTML = `<p class="placeholder-text error" style="color: var(--status-unsealed-color)">${errorMessage}</p>`;
        }
    }

    // Włącz/wyłącz auto-refresh
    toggleAutoRefresh() {
        if (this.autoRefreshEnabled) {
            this.stopAutoRefresh();
        } else {
            this.startAutoRefresh();
        }
    }

    startAutoRefresh() {
        if (!this.autoRefreshURL || this.autoRefreshURL.trim() === "") {
            this.showAlert("Najpierw ustaw URL do pliku CSV", "warning");
            return;
        }

        this.autoRefreshEnabled = true;
        const intervalMs = this.autoRefreshIntervalMinutes * 60 * 1000;

        // Załaduj od razu
        this.fetchCSVFromURL(this.autoRefreshURL);

        // Ustaw interwał
        this.autoRefreshInterval = setInterval(() => {
            console.log("Auto-refresh: pobieranie danych...");
            this.fetchCSVFromURL(this.autoRefreshURL);
        }, intervalMs);

        console.log(
            `Auto-refresh włączony (co ${this.autoRefreshIntervalMinutes} min)`
        );
    }

    stopAutoRefresh() {
        if (this.autoRefreshInterval) {
            clearInterval(this.autoRefreshInterval);
            this.autoRefreshInterval = null;
        }
        this.autoRefreshEnabled = false;
        console.log("Auto-refresh wyłączony");
    }

    setAutoRefreshURL(url) {
        this.autoRefreshURL = url;
        localStorage.setItem("csv_auto_refresh_url", url);
        console.log(`Zapisano URL auto-refresh: ${url}`);
    }

    setAutoRefreshInterval(minutes) {
        this.autoRefreshIntervalMinutes = minutes;
        localStorage.setItem("csv_auto_refresh_interval", minutes);

        // Jeśli auto-refresh jest włączony, zrestartuj z nowym interwałem
        if (this.autoRefreshEnabled) {
            this.stopAutoRefresh();
            this.startAutoRefresh();
        }
        console.log(`Ustawiono interwał auto-refresh: ${minutes} min`);
    }

    promptAndFetchURL() {
        const url = prompt(
            "Podaj URL do pliku CSV:",
            this.autoRefreshURL || "https://example.com/data.csv"
        );
        if (url && url.trim() !== "") {
            this.setAutoRefreshURL(url);
            this.fetchCSVFromURL(url);
        }
    }

    updateAutoRefreshUI() {
        const toggleBtn = document.getElementById("toggle-auto-refresh");
        const urlInput = document.getElementById("auto-refresh-url");
        const intervalInput = document.getElementById("auto-refresh-interval");

        if (this.autoRefreshEnabled) {
            toggleBtn.innerHTML =
                '<i class="fas fa-stop"></i> <span>Wyłącz Auto-Refresh</span>';
            toggleBtn.style.background = "var(--status-unsealed-color)";
        } else {
            toggleBtn.innerHTML =
                '<i class="fas fa-play"></i> <span>Włącz Auto-Refresh</span>';
            toggleBtn.style.background = "";
        }

        // Ustaw wartości w inputach
        if (urlInput) urlInput.value = this.autoRefreshURL;
        if (intervalInput) intervalInput.value = this.autoRefreshIntervalMinutes;
        if (this.elements.useCorsProxyCheckbox)
            this.elements.useCorsProxyCheckbox.checked = this.useCorsProxy;
    }

    getBufferInfo() {
        const bufferInfo = [];
        for (const [fileName, data] of this.csvBuffer.entries()) {
            bufferInfo.push({
                fileName: fileName,
                recordCount: data.data.length,
                timestamp: new Date(data.timestamp).toLocaleString(),
                isCurrent: fileName === this.currentFileName,
            });
        }
        return bufferInfo;
    }

    openBufferModal() {
        this.updateBufferModal();
        this.elements.bufferModal.classList.remove("hidden");
    }

    closeBufferModal() {
        this.elements.bufferModal.classList.add("hidden");
    }

    updateBufferModal() {
        // Aktualizuj informacje o buforze
        this.elements.currentFileName.textContent = this.currentFileName || "brak";
        this.elements.bufferCount.textContent = `${this.csvBuffer.size}/${this.maxBufferSize}`;

        // Wyczyść listę plików
        this.elements.bufferFilesList.innerHTML = "";

        if (this.csvBuffer.size === 0) {
            this.elements.bufferFilesList.innerHTML =
                '<p class="text-center text-muted">Bufor jest pusty</p>';
            return;
        }

        // Dodaj pliki do listy
        const bufferInfo = this.getBufferInfo();
        bufferInfo.forEach((fileInfo) => {
            const fileItem = document.createElement("div");
            fileItem.className = `buffer-file-item ${fileInfo.isCurrent ? "current" : ""
                }`;

            fileItem.innerHTML = `
                <div class="buffer-file-info">
                    <div class="buffer-file-name">${fileInfo.fileName}</div>
                    <div class="buffer-file-details">
                        ${fileInfo.recordCount} rekordów • ${fileInfo.timestamp}
                    </div>
                </div>
                <div class="buffer-file-actions">
                    ${!fileInfo.isCurrent
                    ? `<button class="nav-btn small" onclick="csvAnalyzer.loadFileFromBuffer('${fileInfo.fileName}')"><i class="fas fa-play-circle"></i> <span>Załaduj</span></button>`
                    : ""
                }
                    <button class="nav-btn small danger" onclick="csvAnalyzer.removeFromBuffer('${fileInfo.fileName
                }')"><i class="fas fa-trash"></i> <span>Usuń</span></button>
                </div>
            `;

            this.elements.bufferFilesList.appendChild(fileItem);
        });
    }

    loadFileFromBuffer(fileName) {
        this.loadFromBuffer(fileName);
        this.closeBufferModal();
    }

    removeFromBuffer(fileName) {
        if (this.csvBuffer.has(fileName)) {
            this.csvBuffer.delete(fileName);
            console.log(`Usunięto z bufora: ${fileName}`);

            // Jeśli usuwamy aktualny plik, wyczyść dane
            if (this.currentFileName === fileName) {
                this.currentFileName = null;
                this.data = [];
                this.filteredData = [];
                this.headers = [];
                this.elements.fileName.textContent = "*.CSV";
                this.elements.tableContainer.innerHTML =
                    '<p class="placeholder-text waiting">Oczekiwanie na dane...</p>';
                this.elements.filterContainer.innerHTML =
                    '<div class="vacant-slot"><span>Filtry dostępne po załadowaniu danych</span></div>';
                this.elements.paginationContainer.innerHTML = "";
                this.updateAll();
            }

            this.updateBufferModal();
        }
    }

    clearAllBufferFiles() {
        this.showConfirm(
            "Czy na pewno chcesz wyczyścić cały bufor? Ta operacja nie może być cofnięta.",
            () => {
                this.clearBuffer();

                // Wyczyść dane jeśli były załadowane
                this.data = [];
                this.filteredData = [];
                this.headers = [];
                this.elements.fileName.textContent = "*.CSV";
                this.elements.tableContainer.innerHTML =
                    '<p class="placeholder-text waiting">Oczekiwanie na dane...</p>';
                this.elements.filterContainer.innerHTML =
                    '<div class="vacant-slot"><span>Filtry dostępne po załadowaniu danych</span></div>';
                this.elements.paginationContainer.innerHTML = "";
                this.updateAll();
                this.updateBufferModal();
            },
            null,
            "danger"
        );
    }

    // Metody dla modala zapisu
    openSaveModal() {
        this.updateSaveModal();
        this.updateAutoRefreshUI();
        this.elements.saveModal.classList.remove("hidden");
    }

    closeSaveModal() {
        this.elements.saveModal.classList.add("hidden");
    }

    updateSaveModal() {
        // Aktualizuj liczniki rekordów
        this.elements.totalRecords.textContent = this.data.length;
        this.elements.filteredRecords.textContent = this.filteredData.length;

        // Ustaw domyślną nazwę pliku
        if (!this.elements.exportFilename.value && this.currentFileName) {
            const baseName = this.currentFileName.replace(".csv", "");
            this.elements.exportFilename.value = `${baseName}_export`;
        }

        // Wyłącz przyciski jeśli brak danych
        this.elements.saveAllData.disabled = this.data.length === 0;
        this.elements.saveFilteredData.disabled = this.filteredData.length === 0;
    }

    exportData(type) {
        const dataToExport = type === "all" ? this.data : this.filteredData;

        if (dataToExport.length === 0) {
            this.showAlert("Brak danych do eksportu", "warning");
            return;
        }

        const filename = this.elements.exportFilename.value || "export";
        const includeHeaders = this.elements.includeHeaders.checked;

        // Konwertuj dane do CSV
        const csvContent = this.convertToCSV(dataToExport, includeHeaders);

        // Pobierz plik
        this.downloadCSV(csvContent, `${filename}.csv`);

        console.log(
            `Wyeksportowano ${dataToExport.length} rekordów do pliku ${filename}.csv`
        );
        this.closeSaveModal();
    }

    convertToCSV(data, includeHeaders = true) {
        if (data.length === 0) return "";

        const headers = this.headers;
        let csv = "";

        if (includeHeaders) {
            csv += headers.map((header) => `"${header}"`).join(",") + "\n";
        }

        data.forEach((row) => {
            const values = headers.map((header) => {
                const value = row[header] ?? "";
                const stringValue = String(value);
                if (
                    stringValue.includes(",") ||
                    stringValue.includes('"') ||
                    stringValue.includes("\n")
                ) {
                    return `"${stringValue.replace(/"/g, '""')}"`;
                }
                return stringValue;
            });
            csv += values.join(",") + "\n";
        });

        return csv;
    }

    downloadCSV(csvContent, filename) {
        const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });
        const link = document.createElement("a");

        if (link.download !== undefined) {
            const url = URL.createObjectURL(blob);
            link.setAttribute("href", url);
            link.setAttribute("download", filename);
            link.style.visibility = "hidden";
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
            URL.revokeObjectURL(url);
        }
    }

    saveBufferToStorage() {
        try {
            const bufferData = {};
            for (const [fileName, data] of this.csvBuffer.entries()) {
                bufferData[fileName] = {
                    data: data.data,
                    timestamp: data.timestamp,
                    headers: data.headers,
                };
            }

            const saveData = {
                buffer: bufferData,
                currentFileName: this.currentFileName,
                timestamp: Date.now(),
            };

            localStorage.setItem("csv_analyzer_buffer", JSON.stringify(saveData));
            this.showAlert(
                `Zapisano bufor z ${this.csvBuffer.size} plikami do localStorage`,
                "success"
            );
            console.log("Bufor zapisany do localStorage");
        } catch (error) {
            console.error("Błąd zapisu bufora:", error);
            this.showAlert(
                "Błąd podczas zapisu bufora. Sprawdź konsolę dla szczegółów.",
                "error"
            );
        }
    }

    loadBufferFromStorage() {
        try {
            const savedData = localStorage.getItem("csv_analyzer_buffer");
            if (!savedData) {
                this.showAlert("Brak zapisanego bufora w localStorage", "info");
                return;
            }

            const parseData = JSON.parse(savedData);
            this.csvBuffer.clear();

            for (const [fileName, data] of Object.entries(parseData.buffer)) {
                this.csvBuffer.set(fileName, {
                    data: data.data,
                    timestamp: data.timestamp,
                    headers: data.headers,
                });
            }

            if (
                parseData.currentFileName &&
                this.csvBuffer.has(parseData.currentFileName)
            ) {
                this.loadFromBuffer(parseData.currentFileName);
            }

            this.showAlert(
                `Wczytano bufor z ${this.csvBuffer.size} plikami`,
                "success"
            );
            console.log(
                `Bufor wczytany z localStorage (${this.csvBuffer.size} plików)`
            );

            if (!this.elements.bufferModal.classList.contains("hidden")) {
                this.updateBufferModal();
            }

            this.closeSaveModal();
        } catch (error) {
            console.error("Błąd wczytywania bufora:", error);
            this.showAlert(
                "Błąd podczas wczytywania bufora. Sprawdź konsolę dla szczegółów.",
                "error"
            );
        }
    }

    // Metody dla modala podsumowania
    openSummaryModal() {
        this.updateSummaryModal();
        this.elements.summaryModal.classList.remove("hidden");
    }

    closeSummaryModal() {
        this.elements.summaryModal.classList.add("hidden");
    }

    updateSummaryModal() {
        if (this.filteredData.length === 0) {
            this.elements.summaryModalContent.innerHTML =
                '<p class="placeholder-text empty">Brak danych do podsumowania</p>';
            return;
        }

        const summary = this.getSummaryData();
        this.elements.summaryModalContent.innerHTML = `
            <table class="data-table">
                <thead>
                    <tr><th>Kategoria</th><th>Liczba</th></tr>
                </thead>
                <tbody>
                    ${Object.entries(summary)
                .map(
                    ([key, value]) => `
                        <tr>
                            <td>${key}</td>
                            <td class="highlight-mono">${value}</td>
                        </tr>
                    `
                )
                .join("")}
                </tbody>
            </table>
        `;
    }

    // Metody dla modala szczegółów
    openDetailsModal(rowData) {
        this.elements.detailsModalContent.innerHTML = this.headers
            .map((header) => {
                const { content, classes } = this.getFormattedCellValue(
                    header,
                    rowData[header]
                );
                return `
                <div class="detail-item">
                    <div class="detail-key">${header.replace(
                    "(OPERATOR)",
                    ""
                )}</div>
                    <div class="detail-value ${classes.join(
                    " "
                )}">${content}</div>
                </div>
            `;
            })
            .join("");
        this.elements.detailsModal.classList.remove("hidden");
    }

    closeDetailsModal() {
        this.elements.detailsModal.classList.add("hidden");
    }

    // Metody dla modala potwierdzenia
    closeConfirmModal() {
        this.elements.confirmModal.classList.add("hidden");
        // Wywołaj callback cancel jeśli istnieje
        if (this._confirmCancelCallback) {
            this._confirmCancelCallback();
            this._confirmCancelCallback = null;
        }
    }

    /**
     * Wyświetla alert z niestandardowym stylem
     * @param {string} message - Wiadomość do wyświetlenia
     * @param {string} type - Typ alertu: 'info', 'success', 'warning', 'error'
     * @param {string} title - Tytuł modala (opcjonalny)
     */
    showAlert(message, type = "info", title = null) {
        const icons = {
            info: "fa-circle-info",
            success: "fa-circle-check",
            warning: "fa-triangle-exclamation",
            error: "fa-circle-exclamation",
        };

        const titles = {
            info: "Informacja",
            success: "Sukces",
            warning: "Ostrzeżenie",
            error: "Błąd",
        };

        const iconClass = icons[type] || icons.info;
        const defaultTitle = titles[type] || titles.info;
        const displayTitle = title || defaultTitle;

        // Ustaw tytuł
        this.elements.confirmModalTitle.innerHTML = `<i class="fas ${iconClass}"></i> <span>${displayTitle}</span>`;

        // Ustaw wiadomość
        this.elements.confirmModalMessage.innerHTML = `<i class="fas ${iconClass}"></i>${message}`;
        this.elements.confirmModalMessage.className = `confirm-message alert-${type}`;

        // Ustaw przyciski - tylko OK
        this.elements.confirmModalButtons.innerHTML = `
            <button class="nav-btn btn-primary" id="confirm-ok-btn">
                <i class="fas fa-check"></i> <span>OK</span>
            </button>
        `;

        // Dodaj event listener do przycisku OK
        document.getElementById("confirm-ok-btn").addEventListener("click", () => {
            this.closeConfirmModal();
        });

        // Pokaż modal
        this.elements.confirmModal.classList.remove("hidden");
    }

    /**
     * Wyświetla dialog potwierdzenia
     * @param {string} message - Wiadomość do wyświetlenia
     * @param {function} onConfirm - Callback wywoływany po potwierdzeniu
     * @param {function} onCancel - Callback wywoływany po anulowaniu (opcjonalny)
     * @param {string} type - Typ: 'question', 'warning', 'danger'
     */
    showConfirm(message, onConfirm, onCancel = null, type = "question") {
        const icons = {
            question: "fa-circle-question",
            warning: "fa-triangle-exclamation",
            danger: "fa-octagon-exclamation",
        };

        const titles = {
            question: "Potwierdzenie",
            warning: "Ostrzeżenie",
            danger: "Uwaga!",
        };

        const iconClass = icons[type] || icons.question;
        const title = titles[type] || titles.question;

        // Ustaw tytuł
        this.elements.confirmModalTitle.innerHTML = `<i class="fas ${iconClass}"></i> <span>${title}</span>`;

        // Ustaw wiadomość
        this.elements.confirmModalMessage.innerHTML = `<i class="fas ${iconClass}"></i>${message}`;
        this.elements.confirmModalMessage.className =
            type === "danger"
                ? "confirm-message alert-error"
                : "confirm-message alert-warning";

        // Ustaw przyciski - TAK i NIE
        const yesButtonClass =
            type === "danger" ? "btn-danger danger" : "btn-success";
        this.elements.confirmModalButtons.innerHTML = `
            <button class="nav-btn btn-secondary" id="confirm-cancel-btn">
                <i class="fas fa-times"></i> <span>Nie</span>
            </button>
            <button class="nav-btn ${yesButtonClass}" id="confirm-yes-btn">
                <i class="fas fa-check"></i> <span>Tak</span>
            </button>
        `;

        // Zapisz callback cancel
        this._confirmCancelCallback = onCancel;

        // Dodaj event listenery do przycisków
        document.getElementById("confirm-yes-btn").addEventListener("click", () => {
            this.closeConfirmModal();
            if (onConfirm) onConfirm();
        });

        document
            .getElementById("confirm-cancel-btn")
            .addEventListener("click", () => {
                this.closeConfirmModal();
                if (onCancel) onCancel();
            });

        // Pokaż modal
        this.elements.confirmModal.classList.remove("hidden");
    }

    // Metody dla zakładek
    switchTab(tabName) {
        this.elements.tabBtns.forEach((btn) => btn.classList.remove("active"));
        document
            .querySelectorAll(".tab-content")
            .forEach((content) => content.classList.remove("active"));

        document.querySelector(`[data-tab="${tabName}"]`).classList.add("active");
        document.getElementById(`${tabName}-tab`).classList.add("active");

        if (tabName === "summary") {
            this.updateSummaryTab();
        }
    }

    updateSummaryTab() {
        if (this.filteredData.length === 0) {
            this.elements.summaryTableContainer.innerHTML =
                '<p class="placeholder-text empty">Brak danych do podsumowania</p>';
            return;
        }

        const summary = this.getSummaryData();
        this.elements.summaryTableContainer.innerHTML = `
            <table class="data-table">
                <thead>
                    <tr><th>Kategoria</th><th>Liczba</th></tr>
                </thead>
                <tbody>
                    ${Object.entries(summary)
                .map(
                    ([key, value]) => `
                        <tr class="summary-row ${this.isClickableCategory(key) ? "clickable" : ""
                        }" data-category="${key}">
                            <td>${key}</td>
                            <td class="highlight-mono">${value}</td>
                        </tr>
                    `
                )
                .join("")}
                </tbody>
            </table>
        `;

        this.elements.summaryTableContainer
            .querySelectorAll(".summary-row.clickable")
            .forEach((row) => {
                row.addEventListener("click", () => {
                    const category = row.dataset.category;
                    this.showDetailedView(category);
                });
            });
    }

    isClickableCategory(category) {
        const clickableCategories = [
            "Rekordy",
            "Pełne",
            "Puste",
            "Do rozładunku",
            "NIASST TOTES",
            "NEQP pallets",
            "NEQP AMPAL",
            "IB STORE",
            "OB STORE",
            "OS Trailers",
            "Puste OS-",
            "CEQP Trailers",
        ];
        return clickableCategories.includes(category);
    }

    showDetailedView(category) {
        this.currentDetailedCategory = category; // Zapisz aktualną kategorię
        let filteredData = [];
        let title = "";

        switch (category) {
            case "Puste":
                filteredData = this.filteredData.filter((row) =>
                    this.isEmptyRecord(row)
                );
                title = "Rekordy z brakującymi danymi (>70% pustych pól lub myślników)";
                break;
            case "Do rozładunku":
                filteredData = this.filteredData.filter(this.isItemForDischarge);
                title = "Elementy do rozładunku (ISA, 0994, INBOUND)";
                break;
            case "NIASST TOTES":
                filteredData = this.filteredData.filter((row) =>
                    Object.values(row).some(this.isNEQPTotes)
                );
                title = "Elementy NIASST TOTES";
                break;
            case "NEQP pallets":
                filteredData = this.filteredData.filter((row) =>
                    Object.values(row).some(
                        (v) => typeof v === "string" && /\bNEQP\s+pallets\b/i.test(v)
                    )
                );
                title = "Elementy NEQP pallets";
                break;
            case "NEQP AMPAL":
                filteredData = this.filteredData.filter((row) =>
                    Object.values(row).some(
                        (v) => typeof v === "string" && /\bNEQP\s+AMPALL?\b/i.test(v)
                    )
                );
                title = "Elementy NEQP AMPAL";
                break;
            case "Pełne":
                filteredData = this.filteredData.filter(this.isFullItem);
                title = "Elementy z ładunkiem (w tym IXD)";
                break;
            case "OS Trailers":
                filteredData = this.filteredData.filter((row) => {
                    const hasOS = Object.values(row).some(
                        (v) => typeof v === "string" && /\bOS-/i.test(v)
                    );
                    // Sprawdź różne warianty nazwy pola TYPE
                    const typeValue =
                        row["TYPE"] ||
                        row["Type"] ||
                        row["type"] ||
                        row["LOCATION"] ||
                        row["Location"];
                    const hasType =
                        typeValue &&
                        String(typeValue).trim() !== "" &&
                        String(typeValue).trim() !== "-" &&
                        String(typeValue).trim() !== "—";
                    return hasOS && hasType;
                });
                title = "OS- Trailers (wszystkie naczepy)";
                break;
            case "Puste OS-":
                filteredData = this.filteredData.filter((row) => {
                    const hasOS = Object.values(row).some(
                        (v) => typeof v === "string" && /\bOS-/i.test(v)
                    );
                    // Sprawdź różne warianty nazwy pola TYPE
                    const typeValue =
                        row["TYPE"] ||
                        row["Type"] ||
                        row["type"] ||
                        row["LOCATION"] ||
                        row["Location"];
                    const hasType =
                        typeValue &&
                        String(typeValue).trim() !== "" &&
                        String(typeValue).trim() !== "-" &&
                        String(typeValue).trim() !== "—";
                    // Sprawdź różne warianty nazwy pola LOAD IDENTIFIER
                    const loadValue =
                        row["LOAD IDENTIFIER(S)"] ||
                        row["Load identifier(s)"] ||
                        row["LOAD IDENTIFIERS"];
                    const hasLoadIdentifier =
                        loadValue &&
                        String(loadValue).trim() !== "" &&
                        String(loadValue).trim() !== "-" &&
                        String(loadValue).trim() !== "—";
                    return hasOS && hasType && !hasLoadIdentifier;
                });
                title = "Puste OS- trailers (bez ładunku)";
                break;
            case "CEQP Trailers":
                filteredData = this.filteredData.filter((row) =>
                    Object.values(row).some(
                        (v) => typeof v === "string" && /\bCEQP\b/i.test(v)
                    )
                );
                title = "Elementy CEQP";
                break;
            case "IB STORE":
                filteredData = this.filteredData.filter((row) =>
                    Object.values(row).some(
                        (v) => typeof v === "string" && /\bIB\s+STORE\b/i.test(v)
                    )
                );
                title = "Elementy IB STORE (magazyn wejściowy)";
                break;
            case "OB STORE":
                filteredData = this.filteredData.filter((row) =>
                    Object.values(row).some(
                        (v) => typeof v === "string" && /\bOB\s+STORE\b/i.test(v)
                    )
                );
                title = "Elementy OB STORE (magazyn wyjściowy)";
                break;
            default:
                return;
        }
        this.renderDetailedView(filteredData, title);
    }

    toggleSummaryView() {
        this.summaryViewMode = this.summaryViewMode === "table" ? "tiles" : "table";
        const btn = document.getElementById("summary-view-toggle-btn");
        if (btn) {
            const icon = btn.querySelector("i");
            const text = btn.querySelector("span");
            if (this.summaryViewMode === "tiles") {
                icon.className = "fas fa-table";
                text.textContent = "TABELA";
            } else {
                icon.className = "fas fa-th";
                text.textContent = "KAFELKI";
            }
        }
        // Re-render aktualnego widoku
        const category = this.currentDetailedCategory;
        if (category) {
            this.showDetailedView(category);
        }
    }

    renderDetailedView(data, title) {
        this.currentDetailedViewData = data;
        if (data.length === 0) {
            this.elements.summaryTableContainer.innerHTML = `
                <div class="detailed-view">
                    <div class="detailed-header">
                        <button class="back-btn" onclick="csvAnalyzer.switchTab('summary')"><i class="fas fa-arrow-left"></i> <span>Powrót</span></button>
                        <h3>${title}</h3>
                    </div>
                    <p class="placeholder-text empty">Brak danych do wyświetlenia</p>
                </div>`;
            return;
        }

        const viewToggleIcon =
            this.summaryViewMode === "tiles" ? "fa-table" : "fa-th";
        const viewToggleText =
            this.summaryViewMode === "tiles" ? "TABELA" : "KAFELKI";

        let contentHtml = "";
        if (this.summaryViewMode === "table") {
            const tableBody = data
                .map((row, index) => this.renderRow(row, index))
                .join("");
            contentHtml = `
                <div class="table-wrapper">
                    <table class="data-table">
                        <thead><tr>${this.headers
                    .map(
                        (header) =>
                            `<th>${header.replace("(OPERATOR)", "")}</th>`
                    )
                    .join("")}</tr></thead>
                        <tbody>${tableBody}</tbody>
                    </table>
                </div>`;
        } else {
            const tilesHtml = data
                .map((row, index) => this.renderTile(row, index))
                .join("");
            contentHtml = `<div class="table-wrapper"><div class="tiles-grid">${tilesHtml}</div></div>`;
        }

        this.elements.summaryTableContainer.innerHTML = `
            <div class="detailed-view">
                <div class="detailed-header">
                    <button class="back-btn" onclick="csvAnalyzer.switchTab('summary')"><i class="fas fa-arrow-left"></i> <span>Powrót</span></button>
                    <h3>${title} (${data.length} elementów)</h3>
                    <button id="summary-view-toggle-btn" class="nav-btn" onclick="csvAnalyzer.toggleSummaryView()"><i class="fas ${viewToggleIcon}"></i> <span>${viewToggleText}</span></button>
                </div>
                ${contentHtml}
            </div>`;

        // Dodaj event listenery dla kafelków jeśli jesteśmy w widoku tiles
        if (this.summaryViewMode === "tiles") {
            this.elements.summaryTableContainer
                .querySelectorAll(".data-tile")
                .forEach((tile, index) => {
                    tile.addEventListener("click", () => {
                        if (this.currentDetailedViewData[index]) {
                            this.openDetailsModal(this.currentDetailedViewData[index]);
                        }
                    });
                });
        }
    }

    processData(data) {
        if (!data.length) {
            this.elements.tableContainer.innerHTML = `<p class="placeholder-text empty">Plik nie zawiera danych.</p>`;
            return;
        }
        this.data = data;
        this.headers = Object.keys(data[0]);
        this.setupFilters();
        this.applyFiltersAndSearch();
    }

    setupFilters() {
        // Usuń puste sloty jeśli istnieją
        const vacantSlots =
            this.elements.filterContainer.querySelectorAll(".vacant-slot");
        vacantSlots.forEach((slot) => slot.remove());

        this.elements.filterContainer.innerHTML = "";
        const filterGrid = document.createElement("div");
        filterGrid.className = "filters-grid";
        ["LOCATION", "TYPE", "OWNER (OPERATOR)"].forEach((column) => {
            if (this.headers.includes(column)) {
                const values = [
                    ...new Set(this.data.map((row) => row[column]).filter(Boolean)),
                ].sort();
                filterGrid.appendChild(this.createFilterSelect(column, values));
            }
        });
        filterGrid.appendChild(
            this.createFilterSelect("LOAD_STATUS", ["Pełny", "Pusty"])
        );
        this.elements.filterContainer.appendChild(filterGrid);
    }

    createFilterSelect(column, values) {
        const div = document.createElement("div");
        div.className = "filter-group";
        const label = document.createElement("label");
        label.textContent = column
            .replace(/_/g, " ")
            .replace("(OPERATOR)", "")
            .trim();
        div.appendChild(label);
        const select = document.createElement("select");
        select.innerHTML = `<option value="">Wszystkie</option>${values
            .map((v) => `<option value="${v}">${v}</option>`)
            .join("")}`;
        select.addEventListener("change", () => {
            this.filters[column] = select.value;
            this.applyFiltersAndSearch();
        });
        div.appendChild(select);
        return div;
    }

    handleSearch(term) {
        this.searchTerm = term.toLowerCase();
        this.applyFiltersAndSearch();
    }

    applyFiltersAndSearch() {
        this.filteredData = this.data.filter((row) => {
            const filterMatch = Object.entries(this.filters).every(
                ([column, value]) => {
                    if (!value) return true;
                    if (column === "LOAD_STATUS") {
                        const isFull = this.isFullItem(row);
                        return (
                            (value === "Pełny" && isFull) || (value === "Pusty" && !isFull)
                        );
                    }
                    return (
                        String(row[column]).toLowerCase() === String(value).toLowerCase()
                    );
                }
            );
            const searchMatch = this.searchTerm
                ? Object.values(row).some((val) =>
                    String(val).toLowerCase().includes(this.searchTerm)
                )
                : true;
            return filterMatch && searchMatch;
        });
        this.currentPage = 1;
        this.updateAll();
    }

    updateAll() {
        this.sortTable(this.sortColumn, true);
        this.updateSummary();
        if (this.viewMode === "table") {
            this.renderTable();
        } else {
            this.renderTiles();
        }
        this.renderPagination();
    }

    toggleView() {
        this.viewMode = this.viewMode === "table" ? "tiles" : "table";
        const icon = this.elements.viewToggleBtn.querySelector("i");
        const text = this.elements.viewToggleBtn.querySelector("span");

        if (this.viewMode === "tiles") {
            icon.className = "fas fa-table";
            text.textContent = "TABELA";
        } else {
            icon.className = "fas fa-th";
            text.textContent = "KAFELKI";
        }

        this.updateAll();
    }

    getSummaryData() {
        return {
            Rekordy: this.filteredData.length,
            Pełne: this.filteredData.filter(this.isFullItem).length,
            Puste: this.filteredData.filter((row) => this.isEmptyRecord(row)).length,
            "Do rozładunku": this.countISADischargeItems(),
            "OS Trailers": this.countOSTrailersItems(),
            "Puste OS-": this.countEmptyOSTrailersItems(),
            "CEQP Trailers": this.countCEQPTrailersItems(),
            "NIASST TOTES": this.countNEQPTotesItems(),
            "NEQP pallets": this.countNEQPPalletsItems(),
            "NEQP AMPAL": this.countNEQPAmpalItems(),
            "IB STORE": this.countIBStoreItems(),
            "OB STORE": this.countOBStoreItems(),
        };
    }

    handleSummaryCardClick(e) {
        const card = e.target.closest(".info-card.clickable");
        if (!card) return;

        const category = card.dataset.category;

        if (category === "Rekordy") {
            this.switchTab("data");
        } else if (this.isClickableCategory(category)) {
            this.switchTab("summary");
            this.showDetailedView(category);
        }
    }

    updateSummary() {
        const summaryContainer = this.elements.summaryContainer;
        if (!summaryContainer) return;
        if (this.filteredData.length === 0) {
            summaryContainer.innerHTML = `<p class="placeholder-text waiting">Załaduj dane, aby zobaczyć podsumowanie.</p>`;
            return;
        }

        const summary = this.getSummaryData();
        const summaryIcons = {
            Rekordy: "fa-file-csv",
            Pełne: "fa-truck",
            Puste: "fa-circle-exclamation",
            "Do rozładunku": "fa-dolly",
            "OS Trailers": "fa-trailer",
            "Puste OS-": "fa-box-open",
            "CEQP Trailers": "fa-boxes",
            "NIASST TOTES": "fa-boxes-stacked",
            "NEQP pallets": "fa-pallet",
            "NEQP AMPAL": "fa-tags",
            "IB STORE": "fa-warehouse",
            "OB STORE": "fa-truck-loading",
        };

        summaryContainer.innerHTML = Object.entries(summary)
            .map(([key, value]) => {
                const isClickable = this.isClickableCategory(key);
                const iconClass = summaryIcons[key] || "fa-question-circle";
                return `
                <div class="info-card ${isClickable ? "clickable" : ""
                    }" data-category="${key}">
                    <div class="info-card-icon">
                        <i class="fas ${iconClass}"></i>
                    </div>
                    <div class="info-card-content">
                        <div class="info-card-title">${key}</div>
                        <div class="info-card-value">${value}</div>
                    </div>
                </div>`;
            })
            .join("");
    }

    renderTable() {
        if (this.filteredData.length === 0) {
            this.elements.tableContainer.innerHTML =
                '<p class="placeholder-text empty">Brak danych do wyświetlenia.</p>';
            return;
        }
        this.currentPageData = this.filteredData.slice(
            (this.currentPage - 1) * this.itemsPerPage,
            this.currentPage * this.itemsPerPage
        );
        const tableBody = this.currentPageData
            .map((row, index) => this.renderRow(row, index))
            .join("");
        this.elements.tableContainer.innerHTML = `
            <table class="data-table">
                <thead><tr>${this.headers
                .map(
                    (header) => `
                    <th data-column="${header}">
                        ${header.replace("(OPERATOR)", "")}
                        ${this.sortColumn === header
                            ? this.sortDirection === "asc"
                                ? "▲"
                                : "▼"
                            : ""
                        }
                    </th>`
                )
                .join("")}</tr></thead>
                <tbody>${tableBody}</tbody>
            </table>`;
        this.elements.tableContainer.querySelectorAll("th").forEach((th) => {
            th.addEventListener("click", () => this.sortTable(th.dataset.column));
        });
    }

    renderTiles() {
        if (this.filteredData.length === 0) {
            // Renderuj przykładowe puste kafelki
            const emptyTiles = this.renderEmptyTiles();
            this.elements.tableContainer.innerHTML = `<div class="tiles-grid">${emptyTiles}</div>`;
            return;
        }
        this.currentPageData = this.filteredData.slice(
            (this.currentPage - 1) * this.itemsPerPage,
            this.currentPage * this.itemsPerPage
        );
        const tilesHtml = this.currentPageData
            .map((row, index) => this.renderTile(row, index))
            .join("");
        this.elements.tableContainer.innerHTML = `<div class="tiles-grid">${tilesHtml}</div>`;

        // Dodaj event listenery dla kliknięcia w kafelek
        this.elements.tableContainer
            .querySelectorAll(".data-tile")
            .forEach((tile, index) => {
                tile.addEventListener("click", () => {
                    if (this.currentPageData[index]) {
                        this.openDetailsModal(this.currentPageData[index]);
                    }
                });
            });
    }

    renderEmptyTiles() {
        // Generuj 6 przykładowych pustych kafelków
        const emptyTileTemplate = (index) => `
            <div class="data-tile vacant">
                <div class="tile-header">
                    <div class="tile-title">Brak danych ${index + 1}</div>
                    <div class="tile-badge">EMPTY</div>
                </div>
                <div class="tile-body">
                    <div class="tile-field">
                        <div class="tile-field-label">Pole 1</div>
                        <div class="tile-field-value">—</div>
                    </div>
                    <div class="tile-field">
                        <div class="tile-field-label">Pole 2</div>
                        <div class="tile-field-value">—</div>
                    </div>
                    <div class="tile-field">
                        <div class="tile-field-label">Pole 3</div>
                        <div class="tile-field-value">—</div>
                    </div>
                    <div class="tile-field">
                        <div class="tile-field-label">Pole 4</div>
                        <div class="tile-field-value">—</div>
                    </div>
                    <div class="tile-field">
                        <div class="tile-field-label">Pole 5</div>
                        <div class="tile-field-value">—</div>
                    </div>
                    <div class="tile-field">
                        <div class="tile-field-label">Pole 6</div>
                        <div class="tile-field-value">—</div>
                    </div>
                </div>
            </div>
        `;

        return Array.from({ length: 6 }, (_, i) => emptyTileTemplate(i)).join("");
    }

    renderTile(row, index) {
        const isDischargeItem = this.isDischargeItem(row);
        const isLoadingItem = this.isLoadingItem(row);
        const isLoadedTrailer = this.isLoadedTrailer(row);
        const isEmpty = this.isEmptyRecord(row); // Rekord z brakującymi danymi

        let tileClass = "data-tile";
        if (isEmpty) tileClass += " vacant"; // Puste - czerwony
        else if (isLoadedTrailer) tileClass += " loaded-trailer";
        else if (isDischargeItem) tileClass += " discharge-item";
        else if (isLoadingItem) tileClass += " loading-item";

        // Wybierz najważniejsze pola do wyświetlenia
        const displayFields = this.headers.slice(0, 8); // Pierwsze 8 pól

        // Tytuł kafelka - użyj pierwszego pola lub ID
        const titleValue = row[this.headers[0]] || `Rekord ${index + 1}`;

        // Status - sprawdź czy sealed lub pusty
        const sealValue = row["SEAL"];
        const isSealed =
            sealValue &&
            (String(sealValue).toLowerCase() === "sealed" ||
                (typeof sealValue === "boolean" && sealValue));
        const statusText = isEmpty ? "EMPTY" : isSealed ? "SEALED" : "UNSEALED";
        const statusClass = isEmpty
            ? "status-unsealed"
            : isSealed
                ? "status-sealed"
                : "status-unsealed";

        return `
            <div class="${tileClass}" data-row-index="${index}">
                <div class="tile-header">
                    <div class="tile-title">${this.escapeHtml(
            String(titleValue)
        )}</div>
                    <div class="tile-badge ${statusClass}">${statusText}</div>
                </div>
                <div class="tile-body">
                    ${displayFields
                .map((header) => {
                    const { content, classes } = this.getFormattedCellValue(
                        header,
                        row[header]
                    );
                    return `
                            <div class="tile-field">
                                <div class="tile-field-label">${header.replace(
                        "(OPERATOR)",
                        ""
                    )}</div>
                                <div class="tile-field-value ${classes.join(
                        " "
                    )}">${content}</div>
                            </div>
                        `;
                })
                .join("")}
                </div>
            </div>
        `;
    }

    escapeHtml(text) {
        const div = document.createElement("div");
        div.textContent = text;
        return div.innerHTML;
    }

    renderRow(row, index) {
        const isDischargeItem = this.isDischargeItem(row);
        const isLoadingItem = this.isLoadingItem(row);
        const isLoadedTrailer = this.isLoadedTrailer(row);
        const isEmpty = this.isEmptyRecord(row); // Rekord z brakującymi danymi

        let rowClass = "";
        if (isEmpty) rowClass = "empty-item"; // Pusty - czerwony
        else if (isLoadedTrailer) rowClass = "loaded-trailer";
        else if (isDischargeItem) rowClass = "discharge-item";
        else if (isLoadingItem) rowClass = "loading-item";
        return `<tr class="${rowClass}" data-row-index="${index}">${this.headers
            .map((header) => this.formatCell(header, row[header]))
            .join("")}</tr>`;
    }

    isEmptyRecord(row) {
        // Sprawdź ile pól jest pustych (null, undefined, "", "-", whitespace)
        const values = Object.values(row);
        const emptyCount = values.filter(
            (val) =>
                val === null ||
                val === undefined ||
                val === "" ||
                (typeof val === "string" &&
                    (val.trim() === "" ||
                        val.trim() === "−" ||
                        val.trim() === "-" ||
                        val.trim() === "—"))
        ).length;

        // Jeśli więcej niż 70% pól jest pustych lub myślnikami, uznaj za pusty rekord
        const emptyPercentage = emptyCount / values.length;
        return emptyPercentage > 0.7;
    }

    isDischargeItem = (row) =>
        /\bIB0(0[5-9]|[12][0-9]|3[0-2])\b/i.test(Object.values(row).join(" "));
    isLoadingItem = (row) =>
        /\bOB0(3[3-9]|[4-7][0-9]|8[0-2])\b/i.test(Object.values(row).join(" "));
    isLoadedTrailer(row) {
        const hasOutbound = Object.values(row).some(
            (value) => typeof value === "string" && /\bOUTBOUND\b/i.test(value)
        );
        const sealValue = row["SEAL"];
        const isSealed =
            sealValue &&
            (String(sealValue).toLowerCase() === "sealed" ||
                (typeof sealValue === "boolean" && sealValue));
        return hasOutbound && isSealed;
    }

    isFullItem = (row) => {
        // CEQP items are always considered empty, so check for this first.
        const hasCEQP = Object.values(row).some(
            (value) => typeof value === "string" && /\bCEQP\b/i.test(value)
        );
        if (hasCEQP) {
            return false;
        }

        // Original logic for full items.
        const hasLoadIdentifier = !!row["LOAD IDENTIFIER(S)"];
        const hasIXD = Object.values(row).some((value) => {
            return typeof value === "string" && /\bIXD\b/i.test(value);
        });
        return hasLoadIdentifier || hasIXD;
    };

    isISANumber = (value) =>
        typeof value === "string" && /\bISA\s*\d+/i.test(value);
    isInboundText = (value) =>
        typeof value === "string" && /\bINBOUND\b/i.test(value);
    isNEQPTotes = (value) =>
        typeof value === "string" && /\b(NIASST|TOTES)\b/i.test(value);

    isItemForDischarge = (row) => {
        return Object.values(row).some((value) => {
            if (typeof value !== "string") return false;
            return (
                this.isISANumber(value) ||
                /\b0994\b/i.test(value) ||
                this.isInboundText(value)
            );
        });
    };

    countISADischargeItems = (data = null) =>
        (data || this.filteredData).filter(this.isItemForDischarge).length;
    countNEQPTotesItems = (data = null) =>
        (data || this.filteredData).filter((row) =>
            Object.values(row).some(this.isNEQPTotes)
        ).length;
    countNEQPPalletsItems = (data = null) =>
        (data || this.filteredData).filter((row) =>
            Object.values(row).some(
                (v) => typeof v === "string" && /\bNEQP\s+pallets\b/i.test(v)
            )
        ).length;
    countNEQPAmpalItems = (data = null) =>
        (data || this.filteredData).filter((row) =>
            Object.values(row).some(
                (v) => typeof v === "string" && /\bNEQP\s+AMPALL?\b/i.test(v)
            )
        ).length;
    countOSTrailersItems = (data = null) =>
        (data || this.filteredData).filter((row) => {
            const hasOS = Object.values(row).some(
                (v) => typeof v === "string" && /\bOS-/i.test(v)
            );
            // Sprawdź różne warianty nazwy pola TYPE
            const typeValue =
                row["TYPE"] ||
                row["Type"] ||
                row["type"] ||
                row["LOCATION"] ||
                row["Location"];
            const hasType =
                typeValue &&
                String(typeValue).trim() !== "" &&
                String(typeValue).trim() !== "-" &&
                String(typeValue).trim() !== "—";
            return hasOS && hasType;
        }).length;
    countEmptyOSTrailersItems = (data = null) =>
        (data || this.filteredData).filter((row) => {
            const hasOS = Object.values(row).some(
                (v) => typeof v === "string" && /\bOS-/i.test(v)
            );
            // Sprawdź różne warianty nazwy pola TYPE
            const typeValue =
                row["TYPE"] ||
                row["Type"] ||
                row["type"] ||
                row["LOCATION"] ||
                row["Location"];
            const hasType =
                typeValue &&
                String(typeValue).trim() !== "" &&
                String(typeValue).trim() !== "-" &&
                String(typeValue).trim() !== "—";
            // Sprawdź różne warianty nazwy pola LOAD IDENTIFIER
            const loadValue =
                row["LOAD IDENTIFIER(S)"] ||
                row["Load identifier(s)"] ||
                row["LOAD IDENTIFIERS"];
            const hasLoadIdentifier =
                loadValue &&
                String(loadValue).trim() !== "" &&
                String(loadValue).trim() !== "-" &&
                String(loadValue).trim() !== "—";
            return hasOS && hasType && !hasLoadIdentifier;
        }).length;
    countCEQPTrailersItems = (data = null) =>
        (data || this.filteredData).filter((row) =>
            Object.values(row).some(
                (v) => typeof v === "string" && /\bCEQP\b/i.test(v)
            )
        ).length;
    countIBStoreItems = (data = null) =>
        (data || this.filteredData).filter((row) =>
            Object.values(row).some(
                (v) => typeof v === "string" && /\bIB\s+STORE\b/i.test(v)
            )
        ).length;
    countOBStoreItems = (data = null) =>
        (data || this.filteredData).filter((row) =>
            Object.values(row).some(
                (v) => typeof v === "string" && /\bOB\s+STORE\b/i.test(v)
            )
        ).length;

    getFormattedCellValue(header, value) {
        let content = value ?? "—";
        let classes = [];
        if (header === "TIME IN YARD") {
            content = TimeFormatter.formatTimeString(value);
            classes.push("highlight-mono");
        } else if (header === "SEAL") {
            const isSealed =
                String(value).toLowerCase() === "sealed" ||
                (typeof value === "boolean" && value);
            content = isSealed ? "ZAPLOMBOWANY" : "BRAK";
            classes.push(
                "status-badge",
                isSealed ? "status-sealed" : "status-unsealed"
            );
        } else if (header === "LOAD IDENTIFIER(S)" && content !== "—") {
            classes.push("highlight-mono");
        }
        if (typeof value === "string") {
            if (this.isNEQPTotes(value)) classes.push("neqp-totes-bold");
            if (this.isInboundText(value) || /\bNON-INVENTORY\b/i.test(value))
                classes.push("inbound-discharge-text");
            if (/\bOUTBOUND\b/i.test(value)) classes.push("outbound-loading-text");
            if (/\bVS\d+/i.test(value)) classes.push("vs-trailer-text");
            if (this.isISANumber(value)) classes.push("isa-discharge-text");
            if (/\bSB-/i.test(value)) classes.push("swap-body-text");
            if (/\bOS-/i.test(value)) classes.push("os-trailer-text");
        }
        return { content, classes };
    }

    formatCell(header, value) {
        const { content, classes } = this.getFormattedCellValue(header, value);
        return `<td class="${classes.join(" ")}">${content}</td>`;
    }

    renderPagination() {
        const totalPages = Math.ceil(this.filteredData.length / this.itemsPerPage);
        this.elements.paginationContainer.innerHTML = "";
        if (totalPages <= 1) return;

        const start = (this.currentPage - 1) * this.itemsPerPage + 1;
        const end = Math.min(
            this.currentPage * this.itemsPerPage,
            this.filteredData.length
        );
        this.elements.paginationContainer.innerHTML = `
            <span class="pagination-info">${start}-${end} z ${this.filteredData.length
            }</span>
            <div class="pagination-controls">
                <button id="prev-page" class="nav-btn" ${this.currentPage === 1 ? "disabled" : ""
            }><i class="fas fa-arrow-left"></i> <span>POPRZ.</span></button>
                <span class="page-indicator">Strona ${this.currentPage
            } z ${totalPages}</span>
                <button id="next-page" class="nav-btn" ${this.currentPage === totalPages ? "disabled" : ""
            }><span>NAST.</span> <i class="fas fa-arrow-right"></i></button>
            </div>`;
        document.getElementById("prev-page").addEventListener("click", () => {
            this.currentPage--;
            this.updateAll();
        });
        document.getElementById("next-page").addEventListener("click", () => {
            this.currentPage++;
            this.updateAll();
        });
    }

    sortTable(column, keepDirection = false) {
        if (!keepDirection) {
            this.sortDirection =
                this.sortColumn === column && this.sortDirection === "asc"
                    ? "desc"
                    : "asc";
        }
        this.sortColumn = column;
        this.filteredData.sort((a, b) => {
            const aVal = a[column] ?? "";
            const bVal = b[column] ?? "";
            let comparison = 0;
            if (column === "TIME IN YARD") {
                comparison =
                    TimeFormatter.getMinutes(aVal) - TimeFormatter.getMinutes(bVal);
            } else if (!isNaN(aVal) && !isNaN(bVal) && aVal !== "" && bVal !== "") {
                comparison = Number(aVal) - Number(bVal);
            } else {
                comparison = String(aVal).localeCompare(String(bVal));
            }
            return this.sortDirection === "asc" ? comparison : -comparison;
        });
        if (!keepDirection) {
            this.renderTable();
        }
    }
}

document.addEventListener("DOMContentLoaded", () => new CSVAnalyzer());

3d styles.content:
/* --- SYSTEM VARIABLES [THEME CONFIG] --- */
:root {
    /* LIGHT MODE: Platinum High-Detail */
    --bg-color: #f8f9fa;
    --panel-bg: #ffffff;
    --text-color: #212529;
    --text-muted: #6c757d;
    --highlight: #ff7300;
    --border-color: #dee2e6;
    --border-color-hover: #ccc;
    --pattern-color: rgba(0, 0, 0, 0.04);

    --shadow-drop:
        0.5px 0.5px 0px #d1d5db,
        1px 1px 0px #d1d5db,
        1.5px 1.5px 0px #d1d5db,
        2px 2px 0px #d1d5db,
        2.5px 2.5px 0px #d1d5db,
        3px 3px 0px #d1d5db,
        4px 4px 8px rgba(163, 177, 198, 0.4),
        -1px -1px 2px rgba(255, 255, 255, 0.8);

    --shadow-inset:
        inset 2px 2px 5px rgba(163, 177, 198, 0.5),
        inset -2px -2px 5px rgba(255, 255, 255, 0.8);

    --screen-bg: #f0f0f0;
    --screen-border: #adb5bd;
    --led-green: #28a745;
    --led-yellow: #ff7300;
    --metal-gradient: linear-gradient(145deg, #f3f4f6 0%, #d1d5db 100%);
    --danger-color: #dc3545;

    /* Legacy Aliases & Helpers */
    --orange-rgb: 255, 115, 0;
    --highlight-color: var(--highlight);
    --success-color: var(--led-green);
    --font-main: "JetBrains Mono", "Share Tech Mono", monaco, courier;
    --font-header: "Share Tech Mono", monospace;
}

:root[theme="dark"] {
    /* DARK MODE: Industrial Anodized */
    --bg-color: #1a1a1a;
    --panel-bg: #212121;
    --text-color: #e9ecef;
    --text-muted: #8d8d8d;
    --border-color: #2a2a2a;
    --border-color-hover: #444;
    --highlight: #ff7300;
    --pattern-color: rgba(255, 255, 255, 0.03);

    --shadow-drop:
        0.5px 0.5px 0px #1a1a1a,
        1px 1px 0px #1a1a1a,
        1.5px 1.5px 0px #1a1a1a,
        2px 2px 0px #111111,
        2.5px 2.5px 0px #111111,
        3px 3px 0px #050505,
        6px 6px 12px rgba(0, 0, 0, 0.8);

    --shadow-inset:
        inset 4px 4px 10px rgba(0, 0, 0, 0.9),
        inset -1px -1px 3px rgba(255, 255, 255, 0.05);

    --screen-bg: #050505;
    --screen-border: #2a2a2a;
    --led-green: #28a745;
    --led-yellow: #ff7300;
    --metal-gradient: linear-gradient(135deg, #222 0%, #000 100%);
    --danger-color: #ff003c;

    /* Legacy Aliases & Helpers */
    --highlight-color: var(--highlight);
    --success-color: var(--led-green);
}

/* --- CORE STYLES --- */

* {
    box-sizing: border-box;
    border-radius: 0 !important;
    /* STRICT NO-RADIUS POLICY */
    user-select: none;
    scrollbar-width: none;
    outline: none;
}

body {
    background-color: var(--bg-color);
    background-image:
        linear-gradient(var(--pattern-color) 1px, transparent 1px),
        linear-gradient(90deg, var(--pattern-color) 1px, transparent 1px);
    background-size: 20px 20px;
    color: var(--text-color);
    font-family: var(--font-main);
    margin: 0;
    padding: 20px;
    transition: background 0.3s ease, color 0.3s ease;
    height: 100vh;
    width: 100vw;
    overflow: hidden;
}

/* --- LAYOUT & PANELS --- */

.main-grid-container {
    display: grid;
    width: 100%;
    height: calc(100% - 40px);
    /* Adjusted for body padding */
    grid-template-columns: 3fr 1fr;
    grid-template-rows: 1fr;
    gap: 20px;
    grid-template-areas:
        "visual-feed collaboration"
        "bottom-panels bottom-panels";
}

.panel {
    background: var(--panel-bg);
    border: 1px solid var(--border-color);
    display: flex;
    flex-direction: column;
    overflow: hidden;
    position: relative;
    padding: 0;
    box-shadow: var(--shadow-drop);
    transition: all 0.2s;
}

.panel:hover {
    border-color: var(--border-color-hover);
}

.panel-header {
    background: var(--metal-gradient);
    border-bottom: 1px solid var(--border-color);
    color: var(--text-color);
    padding: 0.8rem 1rem;
    font-family: var(--font-header);
    font-size: 0.9rem;
    text-transform: uppercase;
    letter-spacing: 1px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    z-index: 10;
}

.panel-header-secondary {
    /* Inherits most from panel-header, specific adjustments if needed */
    height: auto;
}

.panel-content {
    flex-grow: 1;
    overflow-y: auto;
    background: var(--panel-bg);
    padding: 0.5rem;
}

/* --- INPUTS & CONTROLS --- */

.input-panel {
    padding: 1rem;
    border-bottom: 1px solid var(--border-color);
    background: var(--panel-bg);
}

.input-group>label {
    font-family: var(--font-header);
    font-size: 0.7rem;
    color: var(--highlight);
    text-transform: uppercase;
    margin-bottom: 0.5rem;
    display: block;
    letter-spacing: 0.5px;
}

.input-row {
    display: flex;
    gap: 10px;
}

/* Styled Input - Inset Look */
.input-row input[type="text"] {
    flex-grow: 1;
    background: var(--bg-color);
    /* Fallback */
    border: 1px solid transparent;
    color: var(--text-color);
    padding: 0.8rem 1rem;
    font-family: var(--font-main);
    font-size: 0.9rem;
    font-weight: 700;
    text-transform: uppercase;
    box-shadow: var(--shadow-inset);
    transition: all 0.2s;
}

.input-row input[type="text"]:focus {
    background-color: var(--screen-bg);
    color: var(--highlight);
    border: 1px solid var(--highlight);
}

.input-row input[type="text"]::placeholder {
    color: var(--text-muted);
    opacity: 0.5;
}

/* Styled Buttons - Drop Shadow Look */
.input-row button,
.file-input-btn,
.playlist-btn,
.nav-btn,
.ctrls button,
.help-button {
    background: var(--panel-bg);
    color: var(--text-muted);
    border: 1px solid var(--border-color);
    padding: 8px 12px;
    cursor: pointer;
    font-family: var(--font-header);
    font-weight: 700;
    text-transform: uppercase;
    box-shadow: var(--shadow-drop);
    transition: all 0.1s;
    display: flex;
    align-items: center;
    justify-content: center;
}

.input-row button:hover,
.file-input-btn:hover,
.playlist-btn:hover,
.nav-btn:hover,
.ctrls button:hover,
.help-button:hover {
    color: var(--highlight);
    border-color: var(--highlight);
    transform: translate(-1px, -1px);
}

.input-row button:active,
.file-input-btn:active,
.playlist-btn:active,
.nav-btn:active,
.ctrls button:active,
.help-button:active {
    box-shadow: var(--shadow-inset);
    transform: translate(1px, 1px);
    color: var(--highlight);
}

.file-input-btn input[type="file"] {
    display: none;
}

/* --- PLAYER AREA --- */

.player-wrapper {
    position: relative;
    flex-grow: 1;
    display: flex;
    flex-direction: column;
    min-height: 0;
    background: #000;
    border-top: 1px solid var(--border-color);
}

.player {
    background: #000;
    flex-grow: 1;
    display: flex;
    flex-direction: column;
    position: relative;
}

.stage {
    flex-grow: 1;
    background: #000;
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
}

video {
    width: 100%;
    height: 100%;
    object-fit: contain;
}

.ctrls {
    background: var(--panel-bg);
    border-top: 1px solid var(--border-color);
    padding: 15px;
    display: flex;
    flex-direction: column;
    gap: 10px;
}

/* Progress Bar */
.progress {
    display: grid;
    gap: 6px;
}

.time {
    display: flex;
    justify-content: space-between;
    color: var(--text-muted);
    font-size: 0.7rem;
    font-family: var(--font-header);
}

#buffer-info {
    color: var(--highlight);
    font-weight: 700;
}

input[type="range"] {
    -webkit-appearance: none;
    appearance: none;
    width: 100%;
    height: 10px;
    background: var(--bg-color);
    box-shadow: var(--shadow-inset);
    outline: none;
    cursor: pointer;
    border: 1px solid var(--border-color);
}

input[type="range"]::-webkit-slider-thumb {
    -webkit-appearance: none;
    width: 10px;
    height: 20px;
    /* Rectangular handle */
    background: var(--highlight);
    border: 1px solid #000;
    cursor: pointer;
    box-shadow: 0 0 5px var(--highlight);
}

/* Controls Grid */
.grid {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.left,
.right {
    display: flex;
    gap: 10px;
    align-items: center;
}

.vol {
    display: flex;
    align-items: center;
    gap: 8px;
}

.vol input[type="range"] {
    width: 80px;
}

#vol-percent {
    font-size: 0.7rem;
    color: var(--text-muted);
    font-family: var(--font-header);
    width: 30px;
}

.right select {
    background: var(--panel-bg);
    color: var(--text-muted);
    border: 1px solid var(--border-color);
    padding: 8px;
    font-family: var(--font-header);
    font-weight: 700;
    box-shadow: var(--shadow-drop);
    cursor: pointer;
}

.right select:hover {
    color: var(--highlight);
}

/* --- PLAYLIST --- */

#playlist-panel {
    grid-area: collaboration;
}

.playlist-controls {
    display: flex;
    flex-direction: column;
    gap: 10px;
    padding-bottom: 10px;
    border-bottom: 1px solid var(--border-color);
    margin-bottom: 10px;
}

.playlist-btn {
    width: 100%;
    justify-content: flex-start;
    gap: 10px;
    font-size: 0.75rem;
}

.playlist-items {
    flex-grow: 1;
    overflow-y: auto;
    padding-right: 5px;
    /* Space for scrollbar if visible */
}

.playlist-empty {
    text-align: center;
    color: var(--text-muted);
    font-size: 0.8rem;
    margin-top: 2rem;
    font-style: italic;
}

.playlist-item {
    background: var(--bg-color);
    border: 1px solid var(--border-color);
    padding: 10px;
    margin-bottom: 8px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 10px;
    transition: all 0.2s;
    position: relative;
}

.playlist-item:hover {
    border-color: var(--highlight);
    box-shadow: var(--shadow-drop);
}

.playlist-item.current {
    background: var(--screen-bg);
    border-color: var(--highlight);
    box-shadow: inset 2px 0 0 var(--highlight);
}

.playlist-item-info {
    flex-grow: 1;
    overflow: hidden;
}

.playlist-item-title {
    font-size: 0.8rem;
    font-weight: 700;
    color: var(--text-color);
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

.playlist-item.current .playlist-item-title {
    color: var(--highlight);
}

.playlist-item-url {
    font-size: 0.65rem;
    color: var(--text-muted);
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

.playlist-item-controls {
    display: flex;
    gap: 5px;
}

.playlist-item-play,
.playlist-item-remove {
    width: 24px;
    height: 24px;
    padding: 0;
    font-size: 0.7rem;
    box-shadow: none;
    /* Flatter look inside list */
    border: 1px solid var(--border-color);
}

.playlist-navigation {
    display: flex;
    align-items: center;
    gap: 10px;
    padding-top: 10px;
    border-top: 1px solid var(--border-color);
}

.nav-btn {
    width: 40px;
    height: 40px;
}

.autoplay-toggle {
    margin-left: auto;
    display: flex;
    align-items: center;
    gap: 8px;
    font-size: 0.7rem;
    color: var(--text-muted);
    cursor: pointer;
}

.autoplay-toggle input {
    accent-color: var(--highlight);
}

/* --- BOTTOM PANELS --- */

#bottom-panels-container {
    grid-area: bottom-panels;
    display: grid;
    grid-template-columns: 3fr 1fr;
    gap: 20px;
}

/* LOG */
.log-entry {
    font-family: "JetBrains Mono", monospace;
    font-size: 0.75rem;
    padding: 2px 0;
    border-bottom: 1px solid rgba(255, 255, 255, 0.05);
    color: var(--text-muted);
}

.log-entry.startup {
    color: var(--highlight);
    font-weight: 700;
}

.log-entry.success {
    color: var(--led-green);
}

.log-entry.error {
    color: var(--danger-color);
}

.clear-log-button {
    border: none;
    background: transparent;
    color: var(--danger-color);
    cursor: pointer;
    box-shadow: none;
}

/* HELP */
.help-buttons-grid {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 10px;
}

.help-button {
    aspect-ratio: 1;
    flex-direction: column;
    position: relative;
}

.help-button i {
    font-size: 1.5rem;
    margin-bottom: 5px;
}

.help-button-top,
.help-button-bottom {
    position: absolute;
    font-size: 0.5rem;
    color: var(--text-muted);
}

.help-button-top {
    top: 4px;
    left: 4px;
}

.help-button-bottom {
    bottom: 4px;
    right: 4px;
}

/* --- MODALS --- */
.modal-overlay {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.8);
    display: none;
    align-items: center;
    justify-content: center;
    z-index: 9999;
    backdrop-filter: blur(2px);
}

.modal-overlay.show {
    display: flex;
}

.modal {
    background: var(--panel-bg);
    border: 1px solid var(--highlight);
    width: 500px;
    max-width: 90%;
    box-shadow: 0 0 30px rgba(0, 0, 0, 0.5);
}

.modal-header {
    background: var(--metal-gradient);
    padding: 1rem;
    border-bottom: 1px solid var(--border-color);
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-weight: 700;
    color: var(--text-color);
}

.modal-content {
    padding: 1.5rem;
    max-height: 70vh;
    overflow-y: auto;
}

.shortcut-item {
    display: flex;
    justify-content: space-between;
    padding: 8px 0;
    border-bottom: 1px dashed var(--border-color);
    color: var(--text-muted);
}

.shortcut-key {
    background: var(--bg-color);
    border: 1px solid var(--border-color);
    padding: 2px 6px;
    font-family: var(--font-header);
    font-size: 0.8rem;
    color: var(--highlight);
}

/* --- NOTIFICATIONS --- */
#notification-container {
    position: fixed;
    bottom: 30px;
    right: 30px;
    display: flex;
    flex-direction: column;
    gap: 10px;
    z-index: 10000;
}

.notification {
    background: var(--panel-bg);
    border: 1px solid var(--highlight);
    padding: 1rem 1.5rem;
    color: var(--highlight);
    font-family: var(--font-header);
    font-weight: 700;
    text-transform: uppercase;
    box-shadow: var(--shadow-drop);
    animation: slideIn 0.3s ease;
}

.notification.error {
    border-color: var(--danger-color);
    color: var(--danger-color);
}

@keyframes slideIn {
    from {
        transform: translateX(100%);
        opacity: 0;
    }

    to {
        transform: translateX(0);
        opacity: 1;
    }
}

/* Responsive */
@media (max-width: 900px) {
    .main-grid-container {
        grid-template-columns: 1fr;
        grid-template-areas:
            "visual-feed"
            "collaboration"
            "bottom-panels";
        overflow-y: auto;
    }

    #bottom-panels-container {
        grid-template-columns: 1fr;
    }

    body {
        overflow-y: auto;
    }
}

styl CSS.content:
        :root {
            --bg-color: #f8f9fa;
            --panel-bg: #ffffff;
            --text-color: #212529;
            --text-muted: #6c757d;
            --highlight: #ff7300;
            --border-color: #dee2e6;
            --border-color-hover: #ccc;

            --shadow-drop: rgba(0, 0, 0, 0.3) 0px 1px 2px 0px, rgba(78, 78, 78, 0.185) 0px 2px 6px 2px;
            --shadow-inset: inset rgba(78, 78, 78, 0.185) 0px 2px 6px 2px, inset rgba(0, 0, 0, 0.3) 0px 1px 2px 0px;

            --shadow-button: rgba(0, 0, 0, 0.3) 0px 1px 2px 0px, rgba(78, 78, 78, 0.185) 0px 2px 6px 2px;


            --screen-bg: #f0f0f0;
            --screen-border: #adb5bd;
            --led-green: #28a745;
            --led-red: #dc3545;
            --metal-gradient: linear-gradient(145deg, #f3f4f6 0%, #d1d5db 100%);

            /* Syntax Highlighting Light */
            --json-key: #0984e3;
            --json-string: #00b894;
            --json-number: #d63031;
            --json-boolean: #6c5ce7;
            --json-null: #b2bec3;
            --json-yellow: #d39e00;
        }

        :root[theme="dark"] {
            --bg-color: #1a1a1a;
            --panel-bg: #212121;
            --text-color: #e9ecef;
            --text-muted: #8d8d8d;
            --highlight: #ff7300;
            --border-color: #2a2a2a;
            --border-color-hover: #444;

            --shadow-drop: rgba(32, 32, 32, 0.4) 0px 0px 0px 2px, rgba(0, 0, 0, 0.65) 0px 4px 6px -1px, rgba(255, 255, 255, 0.08) 0px 1px 0px inset;
            --shadow-inset: inset 0px 0px 0px 2px rgba(32, 32, 32, 0.4), inset 0px 4px 6px -1px rgba(0, 0, 0, 0.65), inset 0px 1px 0px rgba(255, 255, 255, 0.08);

            --shadow-button: rgba(32, 32, 32, 0.4) 0px 0px 0px 2px, rgba(0, 0, 0, 0.65) 0px 4px 6px -1px, rgba(255, 255, 255, 0.08) 0px 1px 0px inset;

            --screen-bg: #0d0d0d;
            --screen-border: #2a2a2a;
            --led-green: #2ecc71;
            --led-red: #ff003c;
            --metal-gradient: linear-gradient(135deg, #2c2c2c 0%, #1a1a1a 100%);

            /* Syntax Highlighting Dark */
            --json-key: #74b9ff;
            --json-string: #55efc4;
            --json-number: #ff7675;
            --json-boolean: #a29bfe;
            --json-null: #dfe6e9;
            --json-yellow: #ad8200;
        }

        /* --- BASE STYLES --- */
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
            border-radius: 0 !important;
            outline: none;
            user-select: none;
            scrollbar-width: none;
            font-family: "JetBrains Mono", "Share Tech Mono", "Consolas", "Monaco", "Courier New", monospace;
            font-feature-settings: "liga" 1, "calt" 1;
            -webkit-font-smoothing: antialiased;
            -moz-osx-font-smoothing: grayscale;
            text-rendering: optimizeLegibility;
        }

        ::selection {
            background: var(--border-color);
            color: var(--text-color);
        }

        body {
            background-color: var(--bg-color);
            color: var(--text-color);
            height: 100vh;
            display: flex;
            flex-direction: column;
            overflow: hidden;
            transition: background-color 0.3s ease;
            font-family: "JetBrains Mono", "Share Tech Mono", "Consolas", "Monaco", "Courier New", monospace;
            font-feature-settings: "liga" 1, "calt" 1, "kern" 1;
            -webkit-font-smoothing: antialiased;
            -moz-osx-font-smoothing: grayscale;
            text-rendering: optimizeLegibility;
            font-variant-ligatures: common-ligatures;
            font-kerning: normal;
        }

        input,
        textarea,
        pre,
        code {
            font-family: "JetBrains Mono", "Share Tech Mono", "Consolas", "Monaco", "Courier New", monospace;
            font-feature-settings: "liga" 1, "calt" 1;
            -webkit-font-smoothing: antialiased;
            -moz-osx-font-smoothing: grayscale;
        }

        /* --- HEADER HUD --- */
        header {
            background: var(--panel-bg);
            box-shadow: var(--shadow-drop);
            padding: 0 1.5rem;
            z-index: 100;
            flex-shrink: 0;
            display: flex;
            align-items: center;
            height: 50px;
            border-bottom: 1px solid var(--border-color);
            justify-content: space-between;
            position: relative;
            margin-bottom: 10px;
        }

        /* Decorative Screws */
        header::after,
        header::before,
        .panel::after,
        .panel::before {
            content: '';
            width: 8px;
            height: 8px;
            background: var(--metal-gradient);
            position: absolute;
            border: 1px solid rgba(0, 0, 0, 0.3);
            box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.2);
            z-index: 101;
        }

        header::before {
            top: 8px;
            left: 8px;
        }

        header::after {
            top: 8px;
            right: 8px;
        }

        .logo {
            font-family: "Orbitron", "JetBrains Mono", "Share Tech Mono", sans-serif;
            font-weight: 700;
            color: var(--highlight);
            letter-spacing: 2px;
            font-size: 1.1rem;
            text-transform: uppercase;
            display: flex;
            align-items: center;
            gap: 12px;
        }

        .nav-tabs {
            display: flex;
            gap: 8px;
            height: 100%;
            align-items: center;
            position: relative;
        }

        .nav-tabs::before {
            content: '';
            position: absolute;
            bottom: 0;
            left: 0;
            right: 0;
            height: 2px;
            background: var(--border-color);
            z-index: 0;
        }

        /* --- BUTTONS (PHYS-BTN) --- */
        .btn-base {
            height: 36px;
            min-width: 36px;
            background: var(--panel-bg);
            box-shadow: var(--shadow-button);
            border: 1px solid var(--border-color);
            display: flex;
            align-items: center;
            justify-content: center;
            cursor: pointer;
            color: var(--text-muted);
            font-size: 0.8rem;
            font-family: "Share Tech Mono", "JetBrains Mono", "Consolas", "Monaco", "Courier New", monospace;
            text-transform: uppercase;
            font-weight: bold;
            transition: all 0.15s;
            padding: 0 16px;
            gap: 8px;
            position: relative;
        }

        .btn-base:hover {
            color: var(--text-color);
            background: var(--bg-color);
        }

        .btn-base:active,
        .btn-base.active {
            box-shadow: var(--shadow-inset);
            color: var(--highlight);

        }



        .nav-tabs .btn-base {
            background: transparent;
            border: none;
            border-bottom: 2px solid transparent;
            position: relative;
            z-index: 1;
            box-shadow: var(--shadow-drop);
        }

        .nav-tabs .btn-base:hover {
            background: var(--bg-color);
            border-bottom-color: var(--border-color);
        }

        .nav-tabs .btn-base.active {
            background: transparent;
            box-shadow: var(--shadow-drop);
            border-bottom-color: var(--highlight);
        }

        .nav-tabs .btn-base.active::after {
            width: 100%;
            height: 2px;
            bottom: -2px;
        }

        .btn-primary {
            color: var(--highlight);
            border-color: var(--border-color);
            opacity: 0.9;
            font-family: "JetBrains Mono", "Share Tech Mono", "Consolas", "Monaco", "Courier New", monospace;
        }

        .btn-danger {
            color: var(--led-red);
        }

        .btn-icon {
            padding: 0;
            font-size: 1rem;
            width: 36px;
        }

        /* --- LAYOUT --- */
        .app-container {
            display: flex;
            flex-direction: column;
            flex: 1;
            overflow: hidden;
            padding: 0 10px 10px 10px;
        }

        .view-section {
            display: none;
            height: 100%;
            width: 100%;
        }

        .view-section.active {
            display: flex;
            gap: 10px;
        }

        /* --- PANELS --- */
        .panel {
            background: var(--panel-bg);
            box-shadow: var(--shadow-drop);
            padding: 1rem;
            display: flex;
            flex-direction: column;
            border: 1px solid var(--border-color);
            position: relative;
            overflow: hidden;
        }

        .panel::before {
            top: 6px;
            left: 6px;
            width: 6px;
            height: 6px;
        }

        .panel::after {
            bottom: 6px;
            right: 6px;
            width: 6px;
            height: 6px;
        }

        .panel-flex-1 {
            flex: 1;
        }

        .panel-w-50 {
            width: 50%;
        }

        .panel-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 1rem;
            padding-bottom: 0.5rem;
            border-bottom: 1px solid var(--border-color);
            font-family: "Orbitron", "JetBrains Mono", "Share Tech Mono", sans-serif;
            font-size: 0.8rem;
            color: var(--text-muted);
            letter-spacing: 1px;
        }

        .panel-header-title::before {
            content: "■ ";
            color: var(--highlight);
            margin-right: 5px;
        }

        /* --- INPUTS --- */
        .form-group {
            margin-bottom: 12px;
        }

        .form-grid {
            display: grid;
            grid-template-columns: 1fr 1fr;
            gap: 10px;
            margin-bottom: 12px;
        }

        label {
            display: block;
            font-size: 0.7rem;
            color: var(--text-muted);
            margin-bottom: 5px;
            text-transform: uppercase;
            background: var(--bg-color);
            background-image: linear-gradient(135deg, var(--screen-bg) 25%, transparent 25%, transparent 50%, var(--screen-bg) 50%, var(--screen-bg) 75%,
                    transparent 75%, transparent);
            background-size: 10px 10px;
        }

        input[type="text"],
        textarea {
            width: 100%;
            background: var(--bg-color);
            border: 1px solid var(--border-color);
            color: var(--json-yellow);
            font-family: "Share Tech Mono", "JetBrains Mono", "Consolas", "Monaco", "Courier New", monospace;
            padding: 8px 12px;
            font-size: 0.85rem;
            box-shadow: var(--shadow-inset);
            transition: all 0.2s;
        }

        input[type="text"]:focus,
        textarea:focus {
            border-bottom: 1px solid var(--highlight);
            color: var(--text-muted);
        }

        /* --- LIST SYSTEM --- */
        .list-container {
            flex: 1;
            overflow-y: auto;
            background: var(--bg-color);
            box-shadow: var(--shadow-inset);
            padding: 10px;
            border: 1px solid var(--border-color);
            margin-top: 10px;
        }

        .list-item {
            background: var(--panel-bg);
            border: 1px solid var(--border-color);
            padding: 10px;
            display: flex;
            justify-content: space-between;
            align-items: center;
            transition: all 0.2s ease-in-out;
            position: relative;
            box-shadow: var(--shadow-drop);
        }

        .list-item:hover {
            background: var(--bg-color);
            background-image: linear-gradient(135deg, var(--screen-bg) 25%, transparent 25%, transparent 50%, var(--screen-bg) 50%, var(--screen-bg) 75%,
                    transparent 75%, transparent);
            background-size: 10px 10px;
        }

        .list-item.editing {
            background: var(--screen-bg);
        }



        .item-meta {
            display: flex;
            flex-direction: column;
            gap: 2px;
        }

        .item-title {
            font-weight: bold;
            font-size: 0.85rem;
            display: flex;
            flex-direction: column;
            align-items: flex-start;
        }

        .item-file {
            font-size: 0.75rem;
            color: var(--text-muted);
        }

        .item-id {
            font-size: 0.65rem;
            color: var(--highlight);
            margin-right: 5px;
            font-family: var(--font-stack);
        }

        .item-actions {
            display: flex;
            gap: 5px;
        }

        /* Grid View */
        .list-wrapper.grid-view {
            display: grid;
            grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
            gap: 10px;
        }

        .grid-view .list-item {
            flex-direction: column;
            align-items: flex-start;
            gap: 10px;
        }

        .grid-view .item-actions {
            width: 100%;
            justify-content: flex-end;
            border-top: 1px solid var(--border-color);
            padding-top: 5px;
            margin-top: 5px;
        }

        /* --- OUTPUT AREA --- */
        .output-screen {
            flex: 1;
            background: var(--screen-bg);
            box-shadow: var(--shadow-inset);
            border: 1px solid var(--border-color);
            padding: 15px;
            overflow: auto;
            color: var(--text-color);
            font-size: 0.8rem;
            line-height: 1.5;
        }

        /* --- TOOLS & RESIZER --- */
        .tools-split {
            display: flex;
            height: 100%;
            gap: 0;
        }

        .resizer {
            width: 12px;
            background: var(--bg-color);
            border-left: 1px solid var(--border-color);
            border-right: 1px solid var(--border-color);
            cursor: col-resize;
            display: flex;
            align-items: center;
            justify-content: center;
            z-index: 10;
        }

        .resizer::after {
            content: '|||';
            color: var(--text-muted);
            font-size: 8px;
            transform: rotate(90deg);
        }

        .monaco-wrapper {
            flex: 1;
            position: relative;
            background: var(--screen-bg);
            box-shadow: var(--shadow-inset);
            border: 1px solid var(--border-color);
            overflow: hidden;
        }

        .tools-textarea {
            width: 100%;
            height: 100%;
            background: transparent;
            border: none;
            color: var(--text-color);
            padding: 10px;
            resize: none;
        }

        /* --- JSON SYNTAX --- */
        .json-key {
            color: var(--json-key);
        }

        .json-string {
            color: var(--json-string);
        }

        .json-number {
            color: var(--json-number);
        }

        .json-boolean {
            color: var(--json-boolean);
        }

        .json-null {
            color: var(--json-null);
            font-style: italic;
        }

        /* --- MODAL --- */
        .modal-overlay {
            position: fixed;
            inset: 0;
            display: none;
            align-items: center;
            justify-content: center;
            z-index: 2000;
        }

        .modal-overlay.active {
            display: flex;
        }

        .modal-box {
            background: var(--panel-bg);
            box-shadow: var(--shadow-drop);
            border: 1px solid var(--border-color);
            width: 400px;
            padding: 2px;
        }

        .modal-header {
            background: rgba(255, 115, 0, 0.1);
            padding: 10px 15px;
            color: var(--highlight);
            font-family: "Orbitron", "JetBrains Mono", "Share Tech Mono", sans-serif;
            font-size: 0.9rem;
            border-bottom: 1px solid var(--border-color);
            display: flex;
            align-items: center;
            gap: 10px;
            background-image: linear-gradient(135deg, var(--screen-bg) 25%, transparent 25%, transparent 50%, var(--screen-bg) 50%, var(--screen-bg) 75%,
                    transparent 75%, transparent);
            background-size: 10px 10px;
        }

        .modal-content {
            padding: 20px;
            color: var(--text-color);
        }

        .modal-footer {
            padding: 10px 15px;
            display: flex;
            justify-content: flex-end;
            gap: 10px;
            background: var(--bg-color);
            border-top: 1px solid var(--border-color);
        }

        /* --- TOAST --- */
        .toast {
            position: fixed;
            bottom: 20px;
            right: 20px;
            background: var(--bg-color);
            border: 1px solid var(--border-color);
            color: var(--text-color);
            padding: 10px 20px;
            display: flex;
            align-items: center;
            gap: 10px;
            box-shadow: var(--shadow-drop);
            transform: translateX(150%);
            transition: transform 0.3s cubic-bezier(0.18, 0.89, 0.32, 1.28);
            z-index: 3000;
            background-image: linear-gradient(135deg, var(--screen-bg) 25%, transparent 25%, transparent 50%, var(--screen-bg) 50%, var(--screen-bg) 75%,
                    transparent 75%, transparent);
            background-size: 10px 10px;
        }

        .toast.show {
            transform: translateX(0);
        }

        .toast i {
            color: var(--highlight);
        }

        /* --- HELP --- */
        .help-list {
            list-style: none;
            padding-left: 10px;
        }

        .help-list li {
            margin-bottom: 8px;
            position: relative;
            padding-left: 15px;
            font-size: 0.9rem;
            color: var(--text-muted);
        }

        .help-list li::before {
            content: '>';
            position: absolute;
            left: 0;
            color: var(--highlight);
        }

        .import-area {
            background: var(--bg-color);
            padding: 10px;
            border: 1px dashed var(--border-color);
            margin-bottom: 15px;
        }

        /* --- RESPONSIVE --- */
        @media (max-width: 900px) {
            .view-section.active {
                flex-direction: column;
            }

            .panel-w-50 {
                width: 100%;
            }

            .tools-split {
                flex-direction: column;
            }

            .resizer {
                height: 12px;
                width: 100%;
                cursor: row-resize;
                border: 1px solid var(--border-color);
                border-width: 1px 0;
            }

            .resizer::after {
                transform: rotate(0);
            }

            header {
                padding: 0 10px;
                flex-wrap: wrap;
                height: auto;
                padding-bottom: 10px;
            }

            .nav-tabs {
                width: 100%;
                justify-content: center;
                margin-top: 10px;
                gap: 4px;
            }

            .nav-tabs .btn-base {
                flex: 1;
                font-size: 0.7rem;
                padding: 0 8px;
            }
        }

        .tech-id {
            position: absolute;
            bottom: 3px;
            right: 20px;
            font-size: 0.55rem;
            color: var(--text-muted);
            opacity: 0.5;
            font-family: "Share Tech Mono", "JetBrains Mono", "Consolas", "Monaco", "Courier New", monospace;
            pointer-events: none;
        }

        .hidden {
            display: none !important;
        }

        .panel-footer {
            display: flex;
            justify-content: flex-start;
            align-items: center;
            padding: 10px;
            border-top: 1px solid var(--border-color);
            gap: 10px;
        }

        .icon {
            display: flex;
            align-items: center;
            justify-content: center;
        }

        .icon i {
            font-size: 1rem;
            color: var(--text-muted);
            transition: color 0.2s;
        }

        .icon:hover i {
            color: var(--text-color);
        }

