:root {
    /* --- COLORS: PALETTE --- */
    --bg-color: #f8f9fa;
    --highlight-color: #f36c00;
    --success-color: #28a745;
    --danger-color: #dc3545;
    --warning-color: #ffc107;
    --info-color: #17a2b8;
    --border-color: #dee2e6;
    --vs-vi-color: #3b82f6;
    --tak-color: #16a34a;
    --nie-color: #dc2626;

    --rgba-warning-color: 255, 193, 7;
    --rgba-success-color: 40, 167, 69;
    --rgba-danger-color: 220, 53, 69;
    --rgba-info-color: 23, 162, 184;
    --rgba-vs-vi-color: 59, 130, 246;
    --rgba-tak-color: 22, 163, 74;
    --rgba-nie-color: 220, 38, 38;

    /* RGB (comma-separated for rgba()) */
    --highlight-rgb: 243, 108, 0;
    --rgba-highlight: 243, 108, 0;
    --rgba-bg-primary: 255, 255, 255;
    --text-color-rgb: 33, 37, 41;
    --text-muted-rgb: 108, 117, 125;
    --bg-rgb: 248, 249, 250;
    --border-color-rgb: 222, 226, 230;

    /* --- COLORS: TEXT SPECIFIC --- */
    --vs-trailer-text: #117a65;
    --r-trailer-text: #2471a3;
    --missing-scac-text: #c54235;
    --visit-inbound-text: #2471a3;
    --visit-outbound-text: #b5830e;

    /* --- SETTINGS --- */
    --font-family: "JetBrains Mono", monaco, courier, monospace;
    --font-main: var(--font-family);
    --transition-speed: 0.2s;
    --screen-bg: #f0f0f0;
    --screen-border: #adb5bd;

    /* --- LIGHT THEME DEFAULTS --- */
    --bg: #f8f9fa;
    --bg-primary: #fff;
    --bg-secondary: #f8f9fa;
    --bg-tertiary: #fff;

    --panel-bg: #f8f9fa;
    --card-bg: #ffffff;
    --input-bg: #f5f5f5;

    --text-color: #212529;
    --text-primary: #212529;
    --text-muted: #6c757d;
    --text-inverse: #fff;

    --border-color-hover: #e0e0e0;
    --hover-bg: #eee;
    --grid-color: rgba(0, 0, 0, 0.03);
    --bg-grid-size: 20px 20px;
    --bg-grid: linear-gradient(var(--grid-color) 1px, transparent 1px),
        linear-gradient(90deg, var(--grid-color) 1px, transparent 1px);

    /* Backgrounds for specific states (light) */
    --tak-bg: rgba(22, 163, 74, 0.1);
    --nie-bg: rgba(220, 38, 38, 0.1);
    --vs-vi-bg: rgba(59, 130, 246, 0.1);
    --highlight-bg: rgba(243, 108, 0, 0.1);
    --latest-bg: rgba(189, 189, 189, 0.15);
    --highlight-soft: var(--highlight-bg);

    --filter-icon: invert(0);
    --filter-icon-hover: invert(24%) sepia(93%) saturate(2130%) hue-rotate(353deg) brightness(97%) contrast(108%);
    --filter-icon-success: invert(55%) sepia(44%) saturate(551%) hue-rotate(89deg) brightness(93%) contrast(97%);
    --filter-icon-danger: invert(36%) sepia(83%) saturate(2030%) hue-rotate(353deg) brightness(97%) contrast(108%);
    --metal-gradient: linear-gradient(165deg, var(--bg-primary) 0%, var(--panel-bg) 45%, var(--border-color-hover) 100%);
    --stripe-accent: rgba(0, 0, 0, 0.06);
    --bg-stripe-pattern: linear-gradient(135deg,
            transparent 0%,
            transparent 25%,
            var(--stripe-accent) 25%,
            var(--stripe-accent) 50%,
            transparent 50%,
            transparent 75%,
            var(--stripe-accent) 75%,
            var(--stripe-accent) 100%);
    --bg-stripe-size: 20px 20px;
    --shadow-drop: rgba(0, 0, 0, 0.3) 0px 1px 2px 0px, rgba(78, 78, 78, 0.185) 0px 2px 6px 2px;
    --shadow-inset: inset rgba(78, 78, 78, 0.185) 0px 2px 6px 2px, inset rgba(0, 0, 0, 0.3) 0px 1px 2px 0px;
    --shadow-hover: rgba(32, 32, 32, 0.2) 0px 0px 0px 1px, rgba(0, 0, 0, 0.325) 0px 2px 3px -0.5px, rgba(255, 255, 255, 0.04) 0px 0.5px 0px inset;

    /* Syntax Highlighting */
    --json-key: var(--highlight-color);
    --json-string: var(--success-color);
    --json-number: #005cc5;
    --json-boolean: var(--danger-color);
}

/* Wyłączenie transition przy zmianie motywu – jedna klatka, bez przycinania */
:root.theme-switching,
:root.theme-switching *,
:root.theme-switching *::before,
:root.theme-switching *::after {
    transition-duration: 0s !important;
    transition-delay: 0s !important;
}

