carrd styles 3d all.content:
@import url(https://fonts.googleapis.com/css2?family=Roboto:wght@400;700&display=swap);

:root {
    --primaryBg: #002b36;
    --cardBgr: #073642;
    --textColor: #b58900;
    --accentColor: #b58900;
    --borderColor: #839496;
    --highlightColor: #586e75;
    /* Scrollbar */
    --scrollbar-width: 4px;
    --scrollbar-track-color: #e0e0e0;
    --scrollbar-thumb-color: #1a1a1a;
    --scrollbar-thumb-radius: 2px;
    /* Kolory podstawowe */
    --color-primary: #ff7300;
    --color-secondary: #6c757d;
    --color-accent: #28a745;
    --color-text-dark: #2c3e50;
    --color-text-light: #f4f6f9;
    /* Tło i czcionki */
    --background-color: rgba(255, 255, 255, 0.089);
    --font-family: "Roboto", sans-serif;
    --font-weight: 400;
    /* Układ */
    --padding: 2rem;
    --container-max-width: 2500px;
    --grid-gap: 1.5rem;
    /* Kategorie */
    --category-bg: rgba(255, 255, 255, 0.116);
    --category-blur: blur(40px);
    --category-border-radius: 12px;
    --category-padding: 1rem;
    --category-box-shadow: 0 15px 25px -4px rgba(0, 0, 0, 0.8), inset 0 -3px 4px -1px rgba(0, 0, 0, 0.6), 0 -10px 15px -1px rgba(82, 82, 82, 0.1), inset 0 3px 4px -1px rgba(255, 255, 255, 0.1), inset 0 0 5px 1px rgba(255, 255, 255, 0.1), inset 0 20px 30px 0 rgba(255, 255, 255, 0.05);
    --box-shadow-hover: 0 7.5px 12.5px -2px rgba(0, 0, 0, 0.4), inset 0 -1.5px 2px -0.5px rgba(0, 0, 0, 0.3), 0 -5px 7.5px -0.5px rgba(41, 41, 41, 0.05), inset 0 1.5px 2px -0.5px rgba(127, 127, 127, 0.05), inset 0 0 2.5px 0.5px rgba(127, 127, 127, 0.05), inset 0 10px 15px 0 rgba(127, 127, 127, 0.025);
    --box-shadow-active: inset 3px 3px 7px var(--color-dark), inset -3px -3px 7px var(--color-light);
    /* Linki */
    --links-list-a-color: #e5ff00c9;
    --links-list-a-bg: rgb(52, 97, 0);
    --links-list-a-hover-color: var(--color-primary);
    /* Responsywność */
    --mobile-grid-columns: 1fr;
    --tablet-grid-columns: repeat(2, 1fr);
    --desktop-grid-columns: repeat(5, 1fr);
}

/* Resetowanie domyślnych stylów */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    scrollbar-width: var(--scrollbar-width);
    scrollbar-color: var(--scrollbar-thumb-color) var(--scrollbar-track-color);
    border-radius: 0 !important;
}

/* Custom scrollbar */
*::-webkit-scrollbar {
    width: var(--scrollbar-width);
}

*::-webkit-scrollbar-track {
    background: var(--scrollbar-track-color);
}

*::-webkit-scrollbar-thumb {
    background-color: var(--scrollbar-thumb-color);
    border-radius: var(--scrollbar-thumb-radius);
}

/* Body styling */
body {
    font-family: var(--font-family);
    background-color: var(--background-color);
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
    background-attachment: fixed;
    font-weight: var(--font-weight);
    color: var(--color-text-dark);
    line-height: 1.6;
}

/* Container */
.container {
    max-width: var(--container-max-width);
    margin: 0 auto;
    padding: var(--padding);
    position: absolute;
    top: 0;
    z-index: 9999;
}

/* Links grid */
.links-grid {
    display: grid;
    gap: var(--grid-gap);
    transition: all 0.3s ease;
}

/* Grid responsiveness */
@media (max-width: 480px) {
    .links-grid {
        grid-template-columns: var(--mobile-grid-columns);
    }
}

@media (min-width: 481px) and (max-width: 768px) {
    .links-grid {
        grid-template-columns: var(--tablet-grid-columns);
    }
}

@media (min-width: 769px) {
    .links-grid {
        grid-template-columns: var(--desktop-grid-columns);
    }
}

/* Categories */
.category {

    backdrop-filter: var(--category-blur);
    border-radius: var(--category-border-radius);
    padding: var(--category-padding);
    box-shadow: var(--category-box-shadow);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    background-image: linear-gradient(45deg, rgba(var(--color-primary), 0.1) 0%, rgba(var(--color-accent), 0.1) 100%), radial-gradient(circle at top right, rgba(var(--color-primary), 0.15) 0%, transparent 60%);
}

.category h2 {
    border-bottom: 1px solid var(--color-secondary);
    padding-bottom: 0.5rem;
    margin-bottom: 1rem;
}

/* Links list */
.links-list {
    list-style: none;
}

.links-list a {
    display: block;
    color: var(--links-list-a-color);
    background: var(--links-list-a-bg);
    box-shadow: var(--category-box-shadow);
    text-decoration: none;
    padding: 0.8rem 1.5rem;
    border-radius: 8px;
    transition: all 0.2s ease;
    position: relative;
    overflow: hidden;
    margin: 10px;
}

.links-list a:hover {
    color: var(--color-accent);
    transform: translateX(8px);
    box-shadow: var(--box-shadow-hover);
}

.links-list a:active {
    box-shadow: var(--box-shadow-active);
}

.links-list a::before {
    content: '→';
    position: absolute;
    left: 10px;
    opacity: 0;
    transition: opacity 0.2s ease;
}

.links-list a:hover::before {
    opacity: 1;
}

/* Animations */
@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(20px);
    }

    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.links-grid>* {
    animation: fadeIn 0.5s ease backwards;
}

/* Dark mode */
@media (prefers-color-scheme: dark) {
    :root {
        --background-color: #121212;
        --category-bg: rgba(30, 30, 30, 0.7);
        --color-text-dark: #e0e0e0;
    }

    body {
        background-color: #121212;
        color: #e0e0e0;
    }
}

.links-list a img {
    float: right;
    margin-left: 8px;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    border: none;
    box-shadow: var(--category-box-shadow);
    padding: 20px;

}

img {
    float: left;
    margin-left: 4px;
    width: 24px;
    height: 24px;
    border-radius: 8px;
    background-color: var(--scrollbar-thumb-color);
    padding: 0;
}

p {
    font-size: 12px;
    color: var(--color-text-dark);
    height: 30px;
    border-radius: 5px;
}

/* Add these rules to your existing style section */
* {
    -webkit-user-drag: none !important;
    -khtml-user-drag: none !important;
    -moz-user-drag: none !important;
    -o-user-drag: none !important;
    user-select: none !important;
    -webkit-user-select: none !important;
    -moz-user-select: none !important;
    -ms-user-select: none !important;
}

/* Remove draggable attributes */
[draggable="true"] {
    -webkit-user-drag: none !important;
}

