// ==UserScript==
// @name         Super Panel Kopiowania (Formatowanie przy Kopiowaniu)
// @namespace    http://tampermonkey.net/
// @version      8.0
// @description  Zaawansowany panel: Szybkie formatowanie (Listy, Wcięcia) przy kopiowaniu, Pływający Notatnik, Szukanie, Tłumaczenie.
// @author       Gemini
// @match        *://*/*
// @grant        GM_setClipboard
// @grant        GM_openInTab
// @grant        GM_addStyle
// @run-at       document-end
// ==/UserScript==

(function () {
    'use strict';

    // --- IKONY SVG ---
    const icons = {
        copy: `<svg viewBox="0 0 24 24" width="16" height="16" stroke="currentColor" stroke-width="2" fill="none" stroke-linecap="round" stroke-linejoin="round"><rect x="9" y="9" width="13" height="13" rx="2" ry="2"></rect><path d="M5 15H4a2 2 0 0 1-2-2V4a2 2 0 0 1 2-2h9a2 2 0 0 1 2 2v1"></path></svg>`,
        html: `<svg viewBox="0 0 24 24" width="16" height="16" stroke="currentColor" stroke-width="2" fill="none" stroke-linecap="round" stroke-linejoin="round"><polyline points="16 18 22 12 16 6"></polyline><polyline points="8 6 2 12 8 18"></polyline></svg>`,
        clean: `<svg viewBox="0 0 24 24" width="16" height="16" stroke="currentColor" stroke-width="2" fill="none" stroke-linecap="round" stroke-linejoin="round"><path d="M12 2v4M12 18v4M4.93 4.93l2.83 2.83M16.24 16.24l2.83 2.83M2 12h4M18 12h4M4.93 19.07l2.83-2.83M16.24 7.76l2.83-2.83"></path></svg>`,
        save: `<svg viewBox="0 0 24 24" width="16" height="16" stroke="currentColor" stroke-width="2" fill="none" stroke-linecap="round" stroke-linejoin="round"><path d="M19 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h11l5 5v11a2 2 0 0 1-2 2z"></path><polyline points="17 21 17 13 7 13 7 21"></polyline><polyline points="7 3 7 8 15 8"></polyline></svg>`,
        search: `<svg viewBox="0 0 24 24" width="16" height="16" stroke="currentColor" stroke-width="2" fill="none" stroke-linecap="round" stroke-linejoin="round"><circle cx="11" cy="11" r="8"></circle><line x1="21" y1="21" x2="16.65" y2="16.65"></line></svg>`,
        translate: `<svg viewBox="0 0 24 24" width="16" height="16" stroke="currentColor" stroke-width="2" fill="none" stroke-linecap="round" stroke-linejoin="round"><circle cx="12" cy="12" r="10"></circle><line x1="2" y1="12" x2="22" y2="12"></line><path d="M12 2a15.3 15.3 0 0 1 4 10 15.3 15.3 0 0 1-4 10 15.3 15.3 0 0 1-4-10 15.3 15.3 0 0 1 4-10z"></path></svg>`,
        wiki: `<svg viewBox="0 0 24 24" width="16" height="16" stroke="currentColor" stroke-width="2" fill="none" stroke-linecap="round" stroke-linejoin="round"><path d="M4 19.5A2.5 2.5 0 0 1 6.5 17H20"></path><path d="M6.5 2H20v20H6.5A2.5 2.5 0 0 1 4 19.5v-15A2.5 2.5 0 0 1 6.5 2z"></path></svg>`,
        yt: `<svg viewBox="0 0 24 24" width="16" height="16" stroke="currentColor" stroke-width="2" fill="none" stroke-linecap="round" stroke-linejoin="round"><rect x="2" y="2" width="20" height="20" rx="5" ry="5"></rect><polygon points="10 8 16 12 10 16 10 8"></polygon></svg>`,
        upper: `<svg viewBox="0 0 24 24" width="16" height="16" stroke="currentColor" stroke-width="2" fill="none" stroke-linecap="round" stroke-linejoin="round"><path d="M7 15l5-10 5 10"></path><line x1="9" y1="13" x2="15" y2="13"></line></svg>`,
        lower: `<svg viewBox="0 0 24 24" width="16" height="16" stroke="currentColor" stroke-width="2" fill="none" stroke-linecap="round" stroke-linejoin="round"><circle cx="12" cy="12" r="4"></circle><line x1="16" y1="8" x2="16" y2="16"></line></svg>`,
        title: `<svg viewBox="0 0 24 24" width="16" height="16" stroke="currentColor" stroke-width="2" fill="none" stroke-linecap="round" stroke-linejoin="round"><path d="M4 7V4h16v3"></path><path d="M9 20h6"></path><path d="M12 4v16"></path></svg>`,
        base64: `<svg viewBox="0 0 24 24" width="16" height="16" stroke="currentColor" stroke-width="2" fill="none" stroke-linecap="round" stroke-linejoin="round"><polyline points="4 7 4 4 20 4 20 7"></polyline><line x1="9" y1="20" x2="15" y2="20"></line><line x1="12" y1="4" x2="12" y2="20"></line></svg>`,
        speak: `<svg viewBox="0 0 24 24" width="16" height="16" stroke="currentColor" stroke-width="2" fill="none" stroke-linecap="round" stroke-linejoin="round"><polygon points="11 5 6 9 2 9 2 15 6 15 11 19 11 5"></polygon><path d="M19.07 4.93a10 10 0 0 1 0 14.14M15.54 8.46a5 5 0 0 1 0 7.07"></path></svg>`,
        qr: `<svg viewBox="0 0 24 24" width="16" height="16" stroke="currentColor" stroke-width="2" fill="none" stroke-linecap="round" stroke-linejoin="round"><rect x="3" y="3" width="7" height="7"></rect><rect x="14" y="3" width="7" height="7"></rect><rect x="14" y="14" width="7" height="7"></rect><rect x="3" y="14" width="7" height="7"></rect></svg>`,
        settings: `<svg viewBox="0 0 24 24" width="24" height="24" stroke="currentColor" stroke-width="2" fill="none" stroke-linecap="round" stroke-linejoin="round"><circle cx="12" cy="12" r="3"></circle><path d="M19.4 15a1.65 1.65 0 0 0 .33 1.82l.06.06a2 2 0 0 1 0 2.83 2 2 0 0 1-2.83 0l-.06-.06a1.65 1.65 0 0 0-1.82-.33 1.65 1.65 0 0 0-1 1.51V21a2 2 0 0 1-2 2 2 2 0 0 1-2-2v-.09A1.65 1.65 0 0 0 9 19.4a1.65 1.65 0 0 0-1.82.33l-.06.06a2 2 0 0 1-2.83 0 2 2 0 0 1 0-2.83l.06-.06a1.65 1.65 0 0 0 .33-1.82 1.65 1.65 0 0 0-1.51-1H3a2 2 0 0 1-2-2 2 2 0 0 1 2-2h.09A1.65 1.65 0 0 0 4.6 9a1.65 1.65 0 0 0-.33-1.82l-.06-.06a2 2 0 0 1 0-2.83 2 2 0 0 1 2.83 0l.06.06a1.65 1.65 0 0 0 1.82.33H9a1.65 1.65 0 0 0 1-1.51V3a2 2 0 0 1 2-2 2 2 0 0 1 2 2v.09a1.65 1.65 0 0 0 1 1.51 1.65 1.65 0 0 0 1.82-.33l.06-.06a2 2 0 0 1 2.83 0 2 2 0 0 1 0 2.83l-.06.06a1.65 1.65 0 0 0-.33 1.82V9a1.65 1.65 0 0 0 1.51 1H21a2 2 0 0 1 2 2 2 2 0 0 1-2 2h-.09a1.65 1.65 0 0 0-1.51 1z"></path></svg>`,
        notes: `<svg viewBox="0 0 24 24" width="16" height="16" stroke="currentColor" stroke-width="2" fill="none" stroke-linecap="round" stroke-linejoin="round"><path d="M4 19.5A2.5 2.5 0 0 1 6.5 17H20"></path><path d="M6.5 2H20v20H6.5A2.5 2.5 0 0 1 4 19.5v-15A2.5 2.5 0 0 1 6.5 2z"></path><path d="M12 6h4"></path><path d="M12 10h4"></path><path d="M12 14h4"></path></svg>`,
        minimize: `<svg viewBox="0 0 24 24" width="14" height="14" stroke="currentColor" stroke-width="3" fill="none"><line x1="4" y1="18" x2="20" y2="18"></line></svg>`,
        close: `<svg viewBox="0 0 24 24" width="14" height="14" stroke="currentColor" stroke-width="3" fill="none"><line x1="18" y1="6" x2="6" y2="18"></line><line x1="6" y1="6" x2="18" y2="18"></line></svg>`,
        list: `<svg viewBox="0 0 24 24" width="16" height="16" stroke="currentColor" stroke-width="2" fill="none" stroke-linecap="round" stroke-linejoin="round"><line x1="8" y1="6" x2="21" y2="6"></line><line x1="8" y1="12" x2="21" y2="12"></line><line x1="8" y1="18" x2="21" y2="18"></line><line x1="3" y1="6" x2="3.01" y2="6"></line><line x1="3" y1="12" x2="3.01" y2="12"></line><line x1="3" y1="18" x2="3.01" y2="18"></line></svg>`,
        num: `<svg viewBox="0 0 24 24" width="16" height="16" stroke="currentColor" stroke-width="2" fill="none" stroke-linecap="round" stroke-linejoin="round"><path d="M10 6h11"></path><path d="M10 12h11"></path><path d="M10 18h11"></path><path d="M4 6h1v4"></path><path d="M4 10h2"></path><path d="M6 18H4c0-1 2-2 2-3s-1-1.5-2-1"></path></svg>`,
        indent: `<svg viewBox="0 0 24 24" width="16" height="16" stroke="currentColor" stroke-width="2" fill="none" stroke-linecap="round" stroke-linejoin="round"><polyline points="15 17 20 12 15 7"></polyline><path d="M4 12h16"></path></svg>`,
        sep: `<svg viewBox="0 0 24 24" width="16" height="16" stroke="currentColor" stroke-width="2" fill="none" stroke-linecap="round" stroke-linejoin="round"><line x1="5" y1="12" x2="19" y2="12"></line></svg>`
    };

    // --- LOGIKA NOTATNIKA (PROSTY) ---
    let userNotes = JSON.parse(localStorage.getItem('scp_notes_store') || '[]');

    function addNote(text) {
        if (!text) return;
        userNotes.push(text);
        localStorage.setItem('scp_notes_store', JSON.stringify(userNotes));
        updateNoteCount();
        if (notesWindow && notesWindow.style.display !== 'none') {
            const area = document.getElementById('scp-notes-text');
            if(area) {
                area.value = getNotesText();
                area.scrollTop = area.scrollHeight;
            }
        }
    }

    function getNotesText() { return userNotes.join('\n\n'); }
    function clearNotes() {
        userNotes = []; localStorage.removeItem('scp_notes_store'); updateNoteCount();
    }
    function updateNoteCount() {
        if (panel) {
            const btn = panel.querySelector('#scp-notes');
            if (btn) btn.innerHTML = `${icons.notes} Notatnik (${userNotes.length})`;
        }
    }

    // --- STYLE ---
    const css = `
        * { -webkit-user-select: text !important; -moz-user-select: text !important; -ms-user-select: text !important; user-select: text !important; }

        #super-copy-panel {
            position: absolute; z-index: 2147483647 !important;
            background-color: #1a1a1a !important; color: #e0e0e0 !important;
            padding: 8px !important; border-radius: 6px !important;
            font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif !important;
            font-size: 13px !important; box-shadow: 0 10px 30px rgba(0,0,0,0.5), 0 0 0 1px #333 !important;
            display: none; flex-direction: column; gap: 8px;
            width: 290px !important; /* Trochę szerszy */
            opacity: 0; transform: translateY(10px); transition: opacity 0.2s, transform 0.2s;
            pointer-events: auto !important; line-height: normal !important; text-align: left !important;
            backdrop-filter: blur(10px);
        }
        #super-copy-panel.visible { opacity: 1 !important; transform: translateY(0) !important; }

        .scp-grid {
            display: grid !important; grid-template-columns: repeat(4, 1fr) !important;
            gap: 6px !important; margin: 0 !important; padding: 0 !important;
        }

        .scp-btn {
            background: #2a2a2a !important; border: 1px solid #3a3a3a !important; border-radius: 4px !important;
            color: #ccc !important; cursor: pointer !important; padding: 8px 4px !important;
            text-align: center !important; transition: all 0.15s !important; display: flex !important;
            flex-direction: column; align-items: center !important; justify-content: center !important;
            font-size: 11px !important; font-weight: 500 !important; box-sizing: border-box !important;
            min-height: 44px !important; line-height: 1.2 !important; gap: 4px !important;
            white-space: nowrap !important; overflow: hidden !important;
        }
        .scp-btn svg { opacity: 0.9; }
        .scp-btn:hover { background: #3a3a3a !important; color: #fff !important; border-color: #555 !important; }
        .scp-btn.primary { background: #0066cc !important; border-color: #005bb5 !important; color: white !important; font-weight: 600 !important; }
        .scp-btn.primary:hover { background: #0077ee !important; }
        .scp-btn.notes-btn { background: #d63384 !important; border-color: #b02a6b !important; color: white !important; font-weight: 600 !important; }
        .scp-btn.notes-btn:hover { background: #e64a96 !important; }

        #scp-qr-container { display: none; justify-content: center; padding: 10px; background: #fff; border-radius: 4px; margin-top: 5px; }
        #scp-qr-img { max-width: 100%; height: auto; display: block; }

        .scp-stats { font-size: 10px !important; color: #777 !important; text-align: right !important; border-top: 1px solid #333 !important; padding-top: 5px !important; margin-top: 2px !important; font-family: monospace !important; }

        #scp-launcher {
            position: fixed !important; bottom: 25px !important; right: 25px !important;
            width: 50px !important; height: 50px !important;
            background: #1a1a1a !important; color: #fff !important;
            border-radius: 50% !important; display: flex !important; align-items: center !important;
            justify-content: center !important; cursor: pointer !important; z-index: 2147483647 !important;
            box-shadow: 0 4px 15px rgba(0,0,0,0.5) !important; border: 1px solid #444 !important;
            opacity: 0.6 !important; transition: all 0.3s !important; user-select: none !important;
        }
        #scp-launcher:hover { opacity: 1 !important; transform: scale(1.1) rotate(90deg) !important; background: #0066cc !important; border-color: #fff !important; }

        /* Window Styling */
        #scp-notes-window {
            position: fixed; top: 20px; right: 20px; width: 360px; height: auto; max-width: 90vw;
            background: #1e1e1e; color: #fff; border-radius: 6px; 
            box-shadow: 0 10px 40px rgba(0,0,0,0.6), 0 0 0 1px #444; display: none; 
            flex-direction: column; z-index: 2147483647; font-family: sans-serif; border: 1px solid #444;
        }
        #scp-notes-header {
            padding: 8px 12px; background: #2a2a2a; border-bottom: 1px solid #444;
            display: flex; justify-content: space-between; align-items: center;
            cursor: move; user-select: none; border-radius: 6px 6px 0 0; font-weight: bold; font-size: 13px;
        }
        .scp-win-controls { display: flex; gap: 8px; }
        .scp-win-btn { cursor: pointer; opacity: 0.7; padding: 2px; display: flex; align-items: center; justify-content: center; }
        .scp-win-btn:hover { opacity: 1; color: #fff; }
        .scp-win-btn.close:hover { color: #ff6b6b; }
        #scp-notes-body { padding: 0; display: flex; flex-direction: column; }
        #scp-notes-text {
            width: 100%; height: 300px; background: #1e1e1e; border: none; color: #ddd; 
            padding: 10px; resize: vertical; font-family: monospace; font-size: 13px; 
            outline: none; box-sizing: border-box; min-height: 100px; line-height: 1.5;
        }
        #scp-notes-footer {
            padding: 8px; border-top: 1px solid #444; background: #2a2a2a;
            display: flex; justify-content: space-between; border-radius: 0 0 6px 6px;
        }
        .scp-footer-btn {
            background: #333; color: #ccc; border: 1px solid #555; padding: 4px 10px; 
            border-radius: 3px; font-size: 11px; cursor: pointer;
        }
        .scp-footer-btn:hover { background: #444; color: #fff; }

        #scp-toast {
            position: absolute; background: #28a745; color: white; padding: 6px 12px;
            border-radius: 20px; font-size: 12px; font-weight: bold; pointer-events: none; opacity: 0;
            transition: opacity 0.3s; z-index: 2147483647; transform: translate(-50%, -100%);
            margin-top: -10px; box-shadow: 0 4px 10px rgba(0,0,0,0.3);
        }

        #scp-notes-window.minimized #scp-notes-body, #scp-notes-window.minimized #scp-notes-footer { display: none; }
        #scp-notes-window.minimized { width: 200px; height: auto !important; resize: none; }
    `;

    const style = document.createElement('style');
    if (style.styleSheet) style.styleSheet.cssText = css;
    else style.appendChild(document.createTextNode(css));
    document.head.appendChild(style);

    // Blokowanie
    const eventsToStop = ['copy', 'cut', 'paste', 'contextmenu', 'selectstart', 'dragstart'];
    eventsToStop.forEach(eventName => document.addEventListener(eventName, e => e.stopPropagation(), true));

    // --- UI ELEMENTS ---

    let panel = null;
    let launcher = null;
    let notesWindow = null;
    let toast = null;
    let currentSelection = "";
    let currentSelectionHTML = "";
    let lastAltActionTime = 0;

    function createLauncher() {
        if (document.getElementById('scp-launcher')) return;
        launcher = document.createElement('div');
        launcher.id = 'scp-launcher';
        launcher.innerHTML = icons.settings;
        launcher.title = "Otwórz Super Panel";
        launcher.addEventListener('mousedown', (e) => { e.preventDefault(); e.stopPropagation(); openPanel(true); });
        document.body.appendChild(launcher);
    }

    function createToast() {
        if(toast) return;
        toast = document.createElement('div');
        toast.id = 'scp-toast';
        toast.innerText = "Dodano do notatek!";
        document.body.appendChild(toast);
    }

    function showToast(x, y) {
        if(!toast) createToast();
        toast.style.left = x + 'px';
        toast.style.top = y + 'px';
        toast.style.opacity = '1';
        setTimeout(() => { toast.style.opacity = '0'; }, 1000);
    }

    function createNotesWindow() {
        if(notesWindow) return;
        notesWindow = document.createElement('div');
        notesWindow.id = 'scp-notes-window';
        notesWindow.innerHTML = `
        <div id="scp-notes-header">
           <span>📒 Notatnik</span>
           <div class="scp-win-controls">
             <div class="scp-win-btn" id="scp-win-min" title="Minimalizuj">${icons.minimize}</div>
             <div class="scp-win-btn close" id="scp-win-close" title="Zamknij">${icons.close}</div>
           </div>
        </div>
        <div id="scp-notes-body"><textarea id="scp-notes-text" placeholder="Tu trafią Twoje notatki..."></textarea></div>
        <div id="scp-notes-footer">
           <button class="scp-footer-btn" id="scp-notes-clear">Wyczyść</button>
           <button class="scp-footer-btn" id="scp-notes-copy">Kopiuj Wszystko</button>
        </div>
      `;
        document.body.appendChild(notesWindow);

        const closeBtn = notesWindow.querySelector('#scp-win-close');
        const minBtn = notesWindow.querySelector('#scp-win-min');
        const clearBtn = notesWindow.querySelector('#scp-notes-clear');
        const copyBtn = notesWindow.querySelector('#scp-notes-copy');
        const area = notesWindow.querySelector('#scp-notes-text');
        const header = notesWindow.querySelector('#scp-notes-header');

        closeBtn.addEventListener('mousedown', (e) => { e.stopPropagation(); notesWindow.style.display = 'none'; });
        minBtn.addEventListener('mousedown', (e) => { e.stopPropagation(); notesWindow.classList.toggle('minimized'); });

        copyBtn.addEventListener('mousedown', (e) => {
            e.stopPropagation(); copyToClipboard(area.value);
            const oldT = copyBtn.innerText; copyBtn.innerText = "Skopiowano!";
            setTimeout(() => copyBtn.innerText = oldT, 1000);
        });
        clearBtn.addEventListener('mousedown', (e) => {
            e.stopPropagation(); if(confirm("Wyczyścić?")) { clearNotes(); area.value = ""; }
        });
        area.addEventListener('input', () => {
            userNotes = [area.value]; localStorage.setItem('scp_notes_store', JSON.stringify(userNotes)); updateNoteCount();
        });

        // Drag
        let isDragging = false; let startX, startY, initialLeft, initialTop;
        header.addEventListener('mousedown', function(e) {
            e.preventDefault(); isDragging = true; startX = e.clientX; startY = e.clientY;
            const rect = notesWindow.getBoundingClientRect(); initialLeft = rect.left; initialTop = rect.top;
            notesWindow.style.right = 'auto'; notesWindow.style.bottom = 'auto';
            notesWindow.style.left = initialLeft + 'px'; notesWindow.style.top = initialTop + 'px';
            document.addEventListener('mousemove', onMouseMove); document.addEventListener('mouseup', onMouseUp);
        });
        function onMouseMove(e) { if (!isDragging) return; notesWindow.style.left = (initialLeft + (e.clientX - startX)) + 'px'; notesWindow.style.top = (initialTop + (e.clientY - startY)) + 'px'; }
        function onMouseUp() { isDragging = false; document.removeEventListener('mousemove', onMouseMove); document.removeEventListener('mouseup', onMouseUp); }
        notesWindow.addEventListener('mousedown', e => e.stopPropagation());
    }

    function openNotes() {
        createNotesWindow();
        const area = document.getElementById('scp-notes-text');
        if (Array.isArray(userNotes)) {
            if (userNotes.length > 1) area.value = userNotes.join('\n\n');
            else if (userNotes.length === 1) area.value = userNotes[0];
            else area.value = "";
        }
        notesWindow.style.display = 'flex'; notesWindow.classList.remove('minimized'); closePanel();
    }

    function createPanel() {
        if (document.getElementById('super-copy-panel')) { panel = document.getElementById('super-copy-panel'); return; }

        panel = document.createElement('div');
        panel.id = 'super-copy-panel';

        panel.innerHTML = `
            <div class="scp-grid">
                <!-- Row 1: Main -->
                <button class="scp-btn primary" id="scp-copy" style="grid-column: span 2" title="Kopiuj tekst">${icons.copy} Kopiuj</button>
                <button class="scp-btn notes-btn" id="scp-notes" style="grid-column: span 2" title="Otwórz Notatnik">${icons.notes} Notatnik</button>

                <!-- Row 2: Structure (NEW) -->
                <button class="scp-btn" id="scp-fmt-list" title="Skopiuj jako listę punktowaną">${icons.list} • Lista</button>
                <button class="scp-btn" id="scp-fmt-num" title="Skopiuj jako listę numerowaną">${icons.num} 1. Lista</button>
                <button class="scp-btn" id="scp-fmt-indent" title="Skopiuj z wcięciem">${icons.indent} ⇥ Wcięcie</button>
                <button class="scp-btn" id="scp-fmt-sep" title="Skopiuj z separatorem">${icons.sep} — Linia</button>

                <!-- Row 3: Standard Tools -->
                <button class="scp-btn" id="scp-html" title="Kopiuj Kod HTML">${icons.html} HTML</button>
                <button class="scp-btn" id="scp-save" title="Zapisz do pliku">${icons.save} Zapisz</button>
                <button class="scp-btn" id="scp-clean" title="Wyczyść formatowanie">${icons.clean} Czysty</button>
                <button class="scp-btn" id="scp-speak" title="Czytaj">${icons.speak} Mowa</button>

                <!-- Row 4: Text Transform -->
                <button class="scp-btn" id="scp-upper" title="WIELKIE LITERY">${icons.upper} UPPER</button>
                <button class="scp-btn" id="scp-lower" title="małe litery">${icons.lower} lower</button>
                <button class="scp-btn" id="scp-title" title="Tytuł">${icons.title} Tytuł</button>
                <button class="scp-btn" id="scp-b64" title="Base64">${icons.base64} Base64</button>

                <!-- Row 5: Search -->
                <button class="scp-btn" id="scp-search" title="Google">${icons.search} Google</button>
                <button class="scp-btn" id="scp-translate" title="Tłumacz">${icons.translate} Tłumacz</button>
                <button class="scp-btn" id="scp-wiki" title="Wikipedia">${icons.wiki} Wiki</button>
                <button class="scp-btn" id="scp-yt" title="YouTube">${icons.yt} YT</button>

                <button class="scp-btn" id="scp-qr" style="grid-column: span 4; margin-top:2px" title="Kod QR">${icons.qr} Generuj Kod QR</button>
            </div>
            <div id="scp-qr-container"></div>
            <div class="scp-stats" id="scp-stats-text">0 zn. | 0 sł.</div>
        `;

        document.body.appendChild(panel);

        const bind = (sel, fn) => panel.querySelector(sel).addEventListener('mousedown', (e) => { e.preventDefault(); e.stopPropagation(); fn(e.target); });

        bind('#scp-copy', (t) => { copyToClipboard(currentSelection); feedback(t, "OK!"); });
        bind('#scp-notes', () => { openNotes(); });

        // NOWE FUNKCJE FORMATOWANIA BEZPOŚREDNIEGO
        bind('#scp-fmt-list', (t) => { 
            const txt = currentSelection.split('\n').map(l => l.trim() ? '- ' + l.trim() : l).join('\n');
            copyToClipboard(txt); feedback(t, "• Lista"); 
        });
        bind('#scp-fmt-num', (t) => { 
            let i = 1;
            const txt = currentSelection.split('\n').map(l => l.trim() ? (i++) + '. ' + l.trim() : l).join('\n');
            copyToClipboard(txt); feedback(t, "1. Lista"); 
        });
        bind('#scp-fmt-indent', (t) => { 
            const txt = currentSelection.split('\n').map(l => '\t' + l).join('\n');
            copyToClipboard(txt); feedback(t, "Wcięcie"); 
        });
        bind('#scp-fmt-sep', (t) => { 
            const txt = currentSelection + "\n-------------------\n";
            copyToClipboard(txt); feedback(t, "Linia"); 
        });

        bind('#scp-html', (t) => { copyToClipboard(currentSelectionHTML || currentSelection); feedback(t, "HTML OK!"); });
        bind('#scp-save', () => { downloadText(currentSelection); closePanel(); });
        bind('#scp-clean', (t) => { copyToClipboard(currentSelection.replace(/\s+/g, ' ').trim()); feedback(t, "Czyste!"); });
        bind('#scp-speak', () => { const u = new SpeechSynthesisUtterance(currentSelection); u.lang = 'pl-PL'; window.speechSynthesis.cancel(); window.speechSynthesis.speak(u); });

        bind('#scp-upper', (t) => { copyToClipboard(currentSelection.toUpperCase()); feedback(t, "UPPER"); });
        bind('#scp-lower', (t) => { copyToClipboard(currentSelection.toLowerCase()); feedback(t, "lower"); });
        bind('#scp-title', (t) => { copyToClipboard(currentSelection.toLowerCase().replace(/(?:^|\s)\w/g, a => a.toUpperCase())); feedback(t, "Tytuł"); });
        bind('#scp-b64', (t) => { try { copyToClipboard(btoa(unescape(encodeURIComponent(currentSelection)))); feedback(t, "B64 OK"); } catch (e) { feedback(t, "Błąd"); } });

        bind('#scp-search', () => openUrl(`https://www.google.com/search?q=${enc(currentSelection)}`));
        bind('#scp-translate', () => openUrl(`https://translate.google.com/?sl=auto&tl=pl&text=${enc(currentSelection)}&op=translate`));
        bind('#scp-wiki', () => openUrl(`https://pl.wikipedia.org/w/index.php?search=${enc(currentSelection)}`));
        bind('#scp-yt', () => openUrl(`https://www.youtube.com/results?search_query=${enc(currentSelection)}`));

        bind('#scp-qr', () => {
            const c = document.getElementById('scp-qr-container');
            if (c.style.display === 'flex') { c.style.display = 'none'; return; }
            c.innerHTML = '...'; c.style.display = 'flex';
            const img = new Image(); img.src = `https://api.qrserver.com/v1/create-qr-code/?size=150x150&data=${enc(currentSelection)}`;
            img.onload = () => { c.innerHTML = ''; c.appendChild(img); };
        });
        panel.addEventListener('mousedown', e => { e.preventDefault(); e.stopPropagation(); });
    }

    // --- HELPERS ---
    function enc(t) { return encodeURIComponent(t); }
    function openUrl(u) { window.open(u, '_blank'); closePanel(); }
    function copyToClipboard(text) {
        if (!text) return;
        if (typeof GM_setClipboard !== 'undefined') GM_setClipboard(text);
        else navigator.clipboard.writeText(text).catch(console.error);
    }
    function downloadText(text) {
        const a = document.createElement('a'); a.href = URL.createObjectURL(new Blob([text], {type:'text/plain'}));
        a.download = 'zaznaczenie.txt'; document.body.appendChild(a); a.click(); document.body.removeChild(a);
    }
    function feedback(target, msg) {
        const btn = target.closest('.scp-btn'); if(!btn) return;
        const oldT = btn.innerHTML; const oldBg = btn.style.background;
        btn.innerText = msg; btn.style.background = '#28a745'; btn.style.color='#fff';
        setTimeout(() => { btn.innerHTML = oldT; btn.style.background = oldBg; btn.style.color=''; }, 800);
    }
    function closePanel() { if(panel) { panel.classList.remove('visible'); setTimeout(() => { if(panel) panel.style.display='none'; }, 200); } }

    function openPanel(force = false) {
        if(!panel) createPanel();
        const selection = window.getSelection();
        const text = selection.toString().trim();

        if(text.length > 0) {
            currentSelection = text;
            if (selection.rangeCount > 0) {
                const div = document.createElement("div"); div.appendChild(selection.getRangeAt(0).cloneContents()); currentSelectionHTML = div.innerHTML;
            }
            if(panel) {
                const charCount = text.length; const wordCount = text.split(/\s+/).filter(w => w.length > 0).length;
                panel.querySelector('#scp-stats-text').innerText = `${charCount} zn. | ${wordCount} sł.`; updateNoteCount();
            }
        } else if (!force) { closePanel(); return; }

        if(force && launcher) {
            const r = launcher.getBoundingClientRect();
            panel.style.position='fixed'; panel.style.bottom=(window.innerHeight-r.top+15)+'px'; panel.style.right='25px'; panel.style.top='auto'; panel.style.left='auto';
        }
        panel.style.display = 'flex'; document.getElementById('scp-qr-container').style.display = 'none'; void panel.offsetWidth; panel.classList.add('visible');
    }

    // --- HANDLERS ---
    document.addEventListener('click', function(e) {
        if (!e.altKey) return; if (Date.now() - lastAltActionTime < 200) return; if (window.getSelection().toString().trim().length > 0) return;
        e.preventDefault(); e.stopPropagation(); let target = e.target;
        if (target.closest('#super-copy-panel') || target.closest('#scp-launcher') || target.closest('#scp-notes-window')) return;
        const text = target.innerText.trim();
        if (text.length > 0) {
            addNote(text); showToast(e.pageX, e.pageY);
            const prevOutline = target.style.outline; const prevTrans = target.style.transition;
            target.style.transition = 'outline 0.1s'; target.style.outline = '3px solid #28a745';
            setTimeout(() => { target.style.outline = prevOutline; target.style.transition = prevTrans; }, 400);
        }
    }, true);

    document.addEventListener('mouseup', function (e) {
        if (panel && panel.contains(e.target)) return; if (launcher && launcher.contains(e.target)) return; if (notesWindow && notesWindow.contains(e.target)) return;
        const selection = window.getSelection(); const text = selection.toString().trim();
        if (e.altKey && text.length > 0) {
            addNote(text); showToast(e.pageX, e.pageY); window.getSelection().removeAllRanges(); closePanel(); lastAltActionTime = Date.now(); return;
        }
        setTimeout(() => {
            const textNow = window.getSelection().toString().trim();
            if (textNow.length > 0) {
                currentSelection = textNow;
                if (window.getSelection().rangeCount > 0) {
                    const div = document.createElement("div"); div.appendChild(window.getSelection().getRangeAt(0).cloneContents()); currentSelectionHTML = div.innerHTML;
                }
                createPanel(); updatePanelContent(textNow); document.getElementById('scp-qr-container').style.display = 'none';
                panel.style.position = 'absolute'; let left = e.pageX + 15; let top = e.pageY + 15;
                if (left + 300 > document.body.scrollWidth) left = e.pageX - 300;
                panel.style.left = left + 'px'; panel.style.top = top + 'px'; panel.style.bottom = 'auto'; panel.style.right = 'auto';
                panel.style.display = 'flex'; void panel.offsetWidth; panel.classList.add('visible');
            } else { closePanel(); }
        }, 10);
    });

    if (document.readyState === 'loading') document.addEventListener('DOMContentLoaded', createLauncher); else createLauncher();
})();

